        return i and (i >= min and i <= max)
    end

    function inRect(p, r)    --p=point={x,y}, r=rectangle={x,y,w,h}
        return (inRange(p[1],r[1],r[1]+r[3]) and inRange(p[2],r[2],r[2]+r[4]))
    end

