            end
    --Y AXIS
            if self.velocity>0 and not self.dead then
                if objAPI:bumpCheck(self.pos.x,self.pos.y,-self.velocity) then
                    self.pos.y=self.pos.y-self.velocity
                    self.velocity=self.velocity*0.7
                else 
