playStage=class()

    function playStage.objLogic()
        if not playStage.pause and not mario.powerUp then
            if mario.pos.x<96 then playStage.spawnOffset=mario.pos.x else playStage.spawnOffset=96 end
            for i=1,#playStage.entityListInner do
                if _G[tostring(playStage.entityListInner[i])]~=nil then
                    if not ((_G[tostring(playStage.entityListInner[i])].pos.x) < (mario.pos.x-128)) and not ((_G[tostring(playStage.entityListInner[i])].pos.x) > (mario.pos.x+320-playStage.spawnOffset)) then --in view distance
                        _G[tostring(playStage.entityListInner[i])]:logic()
                    elseif _G[tostring(playStage.entityListInner[i])].despawnable then
                        if _G[tostring(playStage.entityListInner[i])].pos.x<-16 or _G[tostring(playStage.entityListInner[i])].pos.x < mario.pos.x-136 or ((_G[tostring(playStage.entityListInner[i])].pos.x) > (mario.pos.x+320-playStage.spawnOffset)) then
                            objAPI:destroy(_G[tostring(playStage.entityListInner[i])].classID,_G[tostring(playStage.entityListInner[i])].LEVEL)
                        end
                    end
                end
            end
            for i=1,#playStage.entityListOuter do
                if _G[tostring(playStage.entityListOuter[i])]~=nil then
                    if not ((_G[tostring(playStage.entityListOuter[i])].pos.x) < (mario.pos.x-128)) and not ((_G[tostring(playStage.entityListOuter[i])].pos.x) > (mario.pos.x+320-playStage.spawnOffset)) then
                        _G[tostring(playStage.entityListOuter[i])]:logic() 
                    elseif _G[tostring(playStage.entityListOuter[i])].despawnable then
                        if _G[tostring(playStage.entityListOuter[i])].pos.x<-16 or _G[tostring(playStage.entityListOuter[i])].pos.x < mario.pos.x-136 or ((_G[tostring(playStage.entityListOuter[i])].pos.x) > (mario.pos.x+320-playStage.spawnOffset)) then
                            objAPI:destroy(_G[tostring(playStage.entityListOuter[i])].classID,_G[tostring(playStage.entityListOuter[i])].LEVEL)
    end end end end end end

    function playStage:handleInput()
        local delay=4 --mess of hardcoded shit, sorry
        
        arrowLeftDelay=arrowLeftDelay-1
        arrowRightDelay=arrowRightDelay-1
        arrowDownDelay=arrowDownDelay-1
        
        if arrowUpInput>arrowUpStor then arrowUp=1 else arrowUp=0 end
        if arrowDownInput>arrowDownStor then arrowDown=1 arrowDownDelay=delay+4 else arrowDown=0 end
        if arrowLeftInput>arrowLeftStor then arrowLeft=1 arrowLeftDelay=delay else arrowLeft=0 end
        if arrowRightInput>arrowRightStor then arrowRight=1 arrowRightDelay=delay else arrowRight=0 end
        
        arrowLeftStor=arrowLeftInput
        arrowRightStor=arrowRightInput
        arrowUpStor=arrowUpInput
        arrowDownStor=arrowDownInput
        
        if arrowLeftDelay>0 then arrowLeft=1 end
        if arrowRightDelay>0 then arrowRight=1 end
        if arrowDownDelay>0 then arrowDown=1 end
    end

    function playStage:reset()
        playStage:randomise()
        playStage.cameraOffset=0
        playStage.spawnOffset=0
        mario:resetPos()
    end

    function playStage:randomise()
        for i=1,13 do --y axis
            for i2=0,50 do --x axis
                playStage.permLevel["x"..i2.."y"..i]=math.random(-2,1)
                if math.random(1,15)==1 then playStage.permLevel["x"..i2.."y"..i]=2 end
            end
        end
        playStage.permLevel["x2y12"]=0
        playStage.currentLevel=playStage.permLevel
    end

    function playStage:drawTerrain(gc)
        for i=1,13 do --rendered in rows from left to right - bottom to top
            for i2=math.ceil(playStage.cameraOffset/16),math.ceil((320+playStage.cameraOffset)/16) do --only draw what is visible on screen
                local blockID=getID(i2,i)
                if type(blockID)=='number' then --its a tile
                    if blockID<0 then blockID=0 end
                    if blockIndex[blockID]["texture"][1]~=nil then
                        local frameForAnim=(math.floor((framesPassed/4)%#blockIndex[blockID]["texture"]))+1 --support for animations
                        --print (frameForAnim,blockID,tostring(blockIndex[blockID]["invisible"]),tostring(blockIndex[blockID]["name"]))
                        gc:drawImage(_G[tostring(blockIndex[blockID]["texture"][frameForAnim])], ((i2-1)*16)-playStage.cameraOffset, 212-16*(i)+8) end
                else --load the object
                    objAPI:createObj(blockID,(i2-1)*16,212-16*(i),false)
                    playStage.currentLevel["x"..i2.."y"..i]=0
    end end end end

    function playStage:generate()
        playStage.cameraOffset=0
        playStage.entityListOuter={}
        playStage.entityListInner={}
        for i=1,13 do --y axis
            for i2=0,50 do --x axis
                playStage.permLevel["x"..i2.."y"..i]=0 end end
        for i=1,2 do --y axis
            for i2=0,50 do --x axis
                playStage.permLevel["x"..i2.."y"..i]=1 end end
        for i=3,4 do
            for i2=5,6 do --x axis
                playStage.permLevel["x"..i2.."y"..i]=1 end end --[[
        for i=5,10 do --y axis                                           --lol.
            for i2=1,17 do --x axis
                playStage.permLevel["x"..i2.."y"..i]="mushroom" end end ]]--
        for i=3,5 do --y axis
            for i2=10,11 do --x axis
                playStage.permLevel["x"..i2.."y"..i]=2 end end
        for i=3,6 do --y axis
            for i2=15,16 do --x axis
                playStage.permLevel["x"..i2.."y"..i]=5 end end
        for i=3,7 do --y axis
            for i2=20,21 do --x axis
                playStage.permLevel["x"..i2.."y"..i]=1 end end
        for i=4,4 do --y axis
            for i2=1,2 do --x axis
                playStage.permLevel["x"..i2.."y"..i]=1 end end
        for i=3,3 do --y axis
            for i2=55,61 do --x axis
                playStage.permLevel["x"..i2.."y"..i]=1 end end
        for i=5,6 do --y axis
            for i2=56,60 do --x axis
                playStage.permLevel["x"..i2.."y"..i]="mushroom" end end
        for i=7,7 do --y axis
            for i2=1,2 do --x axis
                playStage.permLevel["x"..i2.."y"..i]=2 end end
        for i=4,13 do --y axis
            for i2=25,25 do --x axis
                playStage.permLevel["x"..i2.."y"..i]=1 end end
        for i=1,7 do --y axis
            for i2=62,73 do --x axis
                playStage.permLevel["x"..i2.."y"..i]=5 end end
        for i=2,7 do --y axis
            for i2=63,72 do --x axis
                playStage.permLevel["x"..i2.."y"..i]="goomba" end end
        for i=6,6 do --y axis
            for i2=42,48 do --x axis
                playStage.permLevel["x"..i2.."y"..i]=2 end end
        playStage.permLevel["x2y12"]=0 playStage.permLevel["x25y6"]=0 playStage.permLevel["x26y8"]=1 playStage.permLevel["x15y7"]="mushroom"
        playStage.permLevel["x55y5"]=1
        playStage.permLevel["x61y5"]=1
        playStage.permLevel["x61y4"]=1
        playStage.permLevel["x55y4"]=1
        playStage.permLevel["x8y2"]=0
        playStage.permLevel["x7y3"]="goomba"
        playStage.permLevel["x13y3"]="goomba"
        playStage.permLevel["x43y7"]="goomba"
        playStage.permLevel["x42y7"]=2
        playStage.permLevel["x48y7"]=2
        playStage.currentLevel=playStage.permLevel
    end

    function playStage:init()
        mario:init()
        playStage.currentLevel={}
        playStage.permLevel={}
        playStage:generate()
        playStage.cameraOffset=0
        mario.active=true
        playStage.active=true
        playStage.pause=false
    end

    function playStage:paint(gc)
        cleanupListDestroy={}
        cleanupListTransfer={}
        if mario.pos.x>96 then playStage.cameraOffset=mario.pos.x-96 end
        if mario.pos.x<96 then playStage.cameraOffset=0 end
        gc:setColorRGB(97, 133, 248)
        gc:fillRect(0,0,320,218) --backdrop
        gc:setColorRGB(255,255,100)   
    
    --logic
        playStage:handleInput()
        playStage:objLogic()
        
    --drawing
        playStage:drawTerrain(gc)
        for i=1,#playStage.entityListInner do
            if not ((_G[tostring(playStage.entityListInner[i])].pos.x) < (mario.pos.x-128)) and not ((_G[tostring(playStage.entityListInner[i])].pos.x) > (mario.pos.x+320)) then
                _G[playStage.entityListInner[i]]:draw(gc) end
        end
        for i=1,#playStage.entityListOuter do
            if not ((_G[tostring(playStage.entityListOuter[i])].pos.x) < (mario.pos.x-128)) and not ((_G[tostring(playStage.entityListOuter[i])].pos.x) > (mario.pos.x+320)) then
                _G[playStage.entityListOuter[i]]:draw(gc) end
        end
        objAPI:cleanup() --transfers layers, destroys queued objects
        
    --mario stuff (seperate to avoid certain issues from objects trying to interact with mario)
        mario:logic()
        mario:draw(gc)
        
        --[[if bumpedBoundary~=false then
            gc:drawRect(bumpedBoundary[1]-playStage.cameraOffset,bumpedBoundary[2]+8,bumpedBoundary[3],bumpedBoundary[4])
        end ]]
end

timer.start(0.01)
playStage=playStage()
print("Running!",versText)

function on.paint(gc)
    if not playStage.pause or mario.powerUp or mario.powerDown or mario.dead then
        framesPassed=framesPassed+1
    end
    playStage:paint(gc)
    gc:drawString("velX: "..mario.velocity.x.." velY: "..mario.velocity.y, 0, 17, top)
    local highlightedx=pixel2plot(mousePos.x,mousePos.y-8)[1]
    local highlightedy=pixel2plot(mousePos.x,mousePos.y-8)[2]
    gc:drawString("marioY: "..mario.pos.y.." entitiesB: "..#playStage.entityListInner.." entitiesF: "..#playStage.entityListOuter, 0, 32, top)
    gc:drawString("blockX "..highlightedx.." blockY "..highlightedy.." id: "..getID(highlightedx,highlightedy).." x"..mousePos.x.."y"..mousePos.y+8, 0, 48, top)
    gc:drawString("v"..versText, 0, 190+20, top)
end
