    function level2string(levelData,TEMPORARY)
        local starts=pixel2plot(levelData.startX,levelData.startY,true)
        local stageSettings="!" local currentBlock={nil,0} local i=0
        if levelData.disableBackScrolling==true         then stageSettings=stageSettings.."s" end
        if levelData.allowBidirectionalSpawning==true   then stageSettings=stageSettings.."b" end
        if levelData.enableGlobalEntities==true         then stageSettings=stageSettings.."g" end
        if levelData.enableShellBouncing==true          then stageSettings=stageSettings.."k" end
        if levelData.enableCoinOnKill==true             then stageSettings=stageSettings.."c" end
        if levelData.enablePowerUpBouncing==true        then stageSettings=stageSettings.."p" end
        if levelData.autoScroll                         then stageSettings=stageSettings..tostring(levelData.autoScroll) end
        if levelData.autoMove                           then stageSettings=stageSettings..levelData.autoMove end
        local STRING="<"..levelData.END.."-v6-"..starts[1].."~"..starts[2].."-"..stageSettings.."-"..levelData.TIME.."-v"..versText.."-"..tostring(versNum).."-my course-"..username..">" --header: end of stage, version of string conversion (for compatibility)
        for y=1,15 do -- y axis, bottom to top, in a column... 14th row initiates theme processing... 15th initiates finalisation
            for x=1,levelData.END do -- x axis, left to right, horizontally per row
                i=i+1
                local ID=levelData["x"..x.."y"..y]
                if y==14 then ID=levelData["t"..x] or 0 end --at the end, start processing of themes
                if ID==currentBlock[1] then -- same as last block
                    currentBlock[2]=currentBlock[2]+1
                end
                if ID~=currentBlock[1] or y==15 then -- begin processing last block
                    if currentBlock[2]>0 then -- apply to string
                        STRING=STRING..","
                        if currentBlock[1]~=0 and currentBlock[1] or currentBlock[2]==1 then STRING=STRING..currentBlock[1] end -- write ID, remains blank if multiple air
                        if currentBlock[2]>1 then -- if multiple of the same
                            STRING=STRING.."*"..string.upper(string.format("%x", currentBlock[2])) -- formats as such: *FF <-- 255 blocks of air
                    end end
                    if y==15 then break end
                    currentBlock={ID,1}
        end end end
        return STRING
    end

    function string2level(STRING,offsetX,offsetY,dataDepth) -- if offsetX is true, then returns table with only metadata
        offsetX=type(offsetX)=="number" and offsetX or 0 offsetY=offsetY or 0
        local levelDataTable,levelData,levelPos={},{},0
        levelDataTable=splitByChar(STRING,",")
        local HEADER=string.sub(levelDataTable[1],2,(#levelDataTable[1]-1))
        HEADER=splitByChar(HEADER,"-")
    -- dataDepth: 1 (for levelList)
        levelData.versText=HEADER[6] or "pre v0.9.0"
        levelData.versNum=tonumber(HEADER[7]) or 41
        levelData.courseName=HEADER[8] or "No Title"
        levelData.author=HEADER[9] or "Unknown"
        if dataDepth==1 then return {levelData.courseName,levelData.versNum,levelData.versText,levelData.author} end
    -- dataDepth: full (playing level)
        levelData.END=  tonumber(HEADER[1])
        levelData.version=       HEADER[2]
        local starts=splitByChar(HEADER[3],"~")
        local stageSettings=     HEADER[4]
        levelData.TIME= tonumber(HEADER[5]) or 500
        starts=plot2pixel(starts[1],starts[2])
        levelData.startX=starts[1]+1 levelData.startY=starts[2]
        if stageSettings and stageSettings~="!" then
            for i=2,#stageSettings do
                local attribute=string.sub(stageSettings,i,i)
                if attribute=="s" then levelData.disableBackScrolling=true
                elseif attribute=="b" then levelData.allowBidirectionalSpawning=true
                elseif attribute=="g" then levelData.enableGlobalEntities=true
                elseif attribute=="k" then levelData.enableShellBouncing=true
                elseif attribute=="c" then levelData.enableCoinOnKill=true
                elseif attribute=="p" then levelData.enablePowerUpBouncing=true
                elseif attribute=="w" or attribute=="r" then levelData.autoMove=attribute
                elseif isInteger(attribute) then levelData.autoScroll=attribute end
            end
        end
        for i=2,#levelDataTable do
            local data=splitByChar(levelDataTable[i],"*")
            if string.sub(levelDataTable[i],1,1)=="*" then table.insert(data,1,nil) end
            data={data[1] or 0,tonumber(data[2] or 1,16)}
            if isInteger(tostring(data[1])) then data[1]=tonumber(data[1]) end
            for i2=1,data[2] do
                levelPos=levelPos+1
                if levelPos<=levelData.END*13 then
                    levelData["x"..(((levelPos-1)%levelData.END)+1+offsetX).."y"..math.ceil(levelPos/levelData.END)+offsetY]=data[1]
                else
                    levelData["t"..(((levelPos-1)%levelData.END)+1+offsetX)]=data[1]
                end
            end
        end
        return levelData
    end

    function copyLevel(levelData)
        return string2level(level2string(levelData))
    end

    function drawFont(gc,text,x,y,position,spacing,backdrop,size,FONT)
        local function countFont(text,spacing) -- this is for font2, small font
            local length=0 local spacing=spacing or 0
            for i=1,#text do
                length=length+((string.lower(string.sub(text,i,i))=="m" or string.lower(string.sub(text,i,i))=="w") and 6 or 5)+spacing
        end return length end
        x=x or 158 y=y or 106 size=size or 1 spacing=spacing or 0 FONT=FONT or "font1"
        local drawOffset=0
        local totalLength=FONT=="font2" and countFont(text,spacing)-1 or (#text*((8*size)+spacing))-1
        if position~=nil then
            if position=="left" then drawOffset=0
            elseif position=="centre" or position=="center" then drawOffset=1-math.ceil(totalLength/2) --british english and american english #_#
            elseif position=="right" then drawOffset=-totalLength
            end
        end
        if backdrop and #text>0 then
            gc:setColorRGB(0,0,0)
            if backdrop=="rgb" then timer2rainbow(gc,framesPassed+200,10) end
            gc:fillRect(x+drawOffset-1,y-1,totalLength+2,10)
        end
        for i=1,#text do
            local letter=string.sub(text,i,i)
            local texture
            if isAlphaNumeric(letter) then
                texture=_G["texture_"..FONT.."_"..string.lower(letter)]
            elseif fontLookup[letter]~=nil then
                texture=_G["texture_"..FONT.."_"..fontLookup[letter]]
            end
            if texture then
                if size~=1 then
                    texture=image.copy(texture,8*size,8*size)
                end
                gc:drawImage(texture,x+drawOffset,y)
            end
            drawOffset=drawOffset+(FONT~="font2" and (8*size)+spacing or countFont(letter,spacing))
        end
    end

    function drawFont2(gc,text,x,y,position,spacing,backdrop,size,FONT) drawFont(gc,text,x,y,position,spacing,backdrop,size,"font2") end

    function drawGUIBox(gc,x,y,w,h,header,text,ignoreLeft) --dont recommend making awfully small boxes
        gc:setPen("thin","smooth") -- i never quite know how to describe these.. vv
        gc:setColorRGB(0,0,0) --outer black border
        gc:drawRect(ignoreLeft and x+2 or x,y,ignoreLeft and w-2 or w,h)
        gc:setColorRGB(255,255,255) --white filling
        gc:fillRect(ignoreLeft and x+2 or x+1,y+1,ignoreLeft and w-2 or w-1,h-1)
        gc:setColorRGB(27,27,27) --dark grey inner box filling
        gc:fillRect(x+3,y+1+(header and 10 or 2),w-5,h-(3+(header and 10 or 2)))
        if header then drawFont(gc,header,x+w/2,y+1,"centre",0,true) end
        gc:setColorRGB(108,108,108) --light grey inner box border
        gc:drawRect(x+2,y+(header and 10 or 2),w-4,h-(2+(header and 10 or 2)))
        if text then
            for i=1,#text do
                drawFont(gc,text[i],x+w/2,y+12+(i-1)*9,"centre",0)
        end end
    end

    function switchTimer(state)
        timerState=state
        if state==true then
            timer.stop()
            timer.start(0.04)
        elseif state==false then
            timer.stop()
            timer.start(0.15) --from my testing, this is slow enough to where the page doesnt freeze when turning off
        else
            switchTimer(not timerState)
        end
    end

    function sTimer(time)  return playStage.framesPassed+time  end --set timer vars
    function cTimer(timer) return timer-playStage.framesPassed end --calculate timer
    function gTimer(timer) return (cTimer(timer)<0)            end --goal timer..? cant think of what to name it
       

