        elseif self.status==3 and (self.deathAnimTimer<playStage.framesPassed) then --stomped
            objAPI:destroy(self.classID,self.LEVEL)
        end
    end
    
    function objGoomba:hit(circumstance)
        if not self.dead then self:handleHitDefault(circumstance,4) end
    end

    function objGoomba:draw(gc,EDITOR)
        if EDITOR==nil then gc:drawImage(_G["texture_"..self.TYPE..self.status],self.x-playStage.cameraOffset,self.y+8)
        else                gc:drawImage(_G["texture_"..EDITOR[3].."1"],EDITOR[1]-editor.cameraOffset,EDITOR[2]+8) end
    end

