    --BUTTON INPUTS
        function playStage:charIn(chr)
            if chr=="−" or chr==" " then playStage:handleInput("action")
            elseif chr=="2" then         playStage:handleInput("down")
            elseif chr=="4" then         playStage:handleInput("left")
            elseif chr=="6" then         playStage:handleInput("right")
            elseif chr=="5" then         playStage:handleInput("up")
            elseif chr=="restart" then
                playStage:generate(level2string(permLevel))
            elseif chr=="edit" and permLevel["x1y1"]~=nil then
                editor:generate()
                editor.active=true
                playStage.active=false
                editor.cameraOffset=math.floor(playStage.cameraOffset/20)*20
            end
            if debug then --these are mostly legacy functions, prevailing from the most early versions of nsmm
                if chr=="1" then
                    mario:powerUpMario()
                elseif chr=="8" then
                    mario.x=mario.x-1
                elseif chr=="9" then
                    mario.x=mario.x+1
                elseif chr=="0" then
                    mario:powerDownMario()
                elseif chr=="r" and permLevel["x1y1"]~=nil then playStage:reset()
                elseif chr=="f" then frameByFrame=not frameByFrame
                elseif chr=="y" then mario.x=73*16
                elseif chr=="d" then
                    debug=not debug
                elseif chr=="v" then
                    on.mouseDown()
                elseif chr=="c" then
                    print(level2string(permLevel))
                elseif chr=="s" then
                    clipboard.addText(level2string(permLevel))
                elseif chr=="l" then
                    local PASTE=clipboard.getText()
                    if string.sub(PASTE,1,1)=="<" then --very crude for now
                        playStage:generate(PASTE)
                        mario:resetPos()
                    end
                elseif chr=="j" then
                    blockSelectionTEMP=blockSelectionTEMP-1
                elseif chr=="k" then
                    blockSelectionTEMP=blockSelectionTEMP+1
                elseif chr=="p" then
                    playStage:PAUSE()
                end
            end
        end
        function playStage:escapeKey()
            if not mario.dead then playStage:PAUSE() end
        end
        function playStage:arrowUp()
            playStage:handleInput("up")
        end
        function playStage:arrowDown()
            playStage:handleInput("down")
        end
        function playStage:arrowLeft()
            playStage:handleInput("left")
        end
        function playStage:arrowRight()
            playStage:handleInput("right")
        end
        function playStage:enterKey()
            if playStage.EDITOR then playStage:charIn("edit") end
        end
        function playStage:mouseDown()
            if debug then
                local placeXY=pixel2plot(mouse.x,mouse.y-8)
                currentLevel["x"..placeXY[1].."y"..placeXY[2]]=blockSelectionListTEMP[(blockSelectionTEMP%(#blockSelectionListTEMP))+1]
                permLevel["x"..placeXY[1].."y"..placeXY[2]]=blockSelectionListTEMP[(blockSelectionTEMP%(#blockSelectionListTEMP))+1]
            end
            if playStage.EDITOR and checkCollision(mouse.x,mouse.y,1,1,4,178,40,30) then
                playStage:charIn("edit")
            end
        end
        function playStage:rightMouseDown()
            if debug then
                local placeXY=pixel2plot(mouse.x,mouse.y-8)
                currentLevel["x"..placeXY[1].."y"..placeXY[2]]=0
                permLevel["x"..placeXY[1].."y"..placeXY[2]]=0
            end
        end

    function playStage:PAUSE() --true/false
        gui:clear()
        if playStage.EDITOR then gui:createPrompt("PAUSED",nil,{{"RESUME","close"},{"EDIT","play_edit"},{"QUIT","quit"}},false)
        else                     gui:createPrompt("PAUSED",nil,{{"RESUME","close"},{"RETRY","play_retry"},{"EDIT","play_edit"},{"QUIT","quit"}},false)
        end
    end

    function playStage:handleInput(INPUT)
        if INPUT~=nil then
            input[INPUT]=1
            if     INPUT=="down" then             input.stor.down=8
            elseif INPUT=="up" then               input.stor.up=2
            elseif INPUT=="action" then           input.stor.action=2
            elseif not currentLevel.autoMove then input.stor[INPUT]=6
            end
        else
            input.stor.up=input.stor.up-1
            input.stor.down=input.stor.down-1
            input.stor.left=input.stor.left-1
            input.stor.right=input.stor.right-1
            input.stor.action=input.stor.action-1
            
            if currentLevel.autoMove then input.left=0 input.right=1
            else
                if input.stor.left>0 then input.left=1 else input.left=0 end
                if input.stor.right>0 then input.right=1 else input.right=0 end
            end

            if input.stor.up>0 then input.up=1 else input.up=0 end
            if input.stor.down>0 then input.down=1 else input.down=0 end
            if input.stor.action>0 then input.action=1 else input.action=0 end
        end
    end

    function playStage:reset() playStage:randomise() end 

    function playStage:randomise() --entertaining but ultimately useless
        for i=1,13 do --y axis
            for i2=0,50 do --x axis
                local RANDOM=math.random(-2,1)
                if RANDOM<0 then RANDOM=0 end
                permLevel["x"..i2.."y"..i]=RANDOM
                if math.random(1,15)==1 then permLevel["x"..i2.."y"..i]=2 end
            end
        end
        local starts=plot2pixel(3,5)
        permLevel.startX=starts[1] permLevel.startY=starts[2] 
        permLevel["x2y5"]=0
        playStage:generate(level2string(permLevel))
    end

    function playStage:drawTerrain(gc) --rendered in rows from bottom to top w/ the rows from left to right
        for i2=math.ceil(playStage.cameraOffset/16),math.ceil((318+playStage.cameraOffset)/16) do --left to right, horizontally, only draw what is visible on screen
            local THEME=plot2theme(i2)
            for i=1,13 do --bottom to top, vertically (row 14 is reserved for hud/special events and is not drawn)
                local blockID=getID(i2,i)
                if type(blockID)=='number' then --its a tile
                    if blockID<0 then blockID=0 end
                    local frameForAnim=(math.floor((playStage.framesPassedBlock/4)%#blockIndex[blockID]["texture"]))+1 --(support for animations)
                    if blockIndex[blockID]["theme"][THEME]~=nil then
                        gc:drawImage(_G[tostring(blockIndex[blockID]["theme"][THEME])], ((i2-1)*16)-playStage.cameraOffset, 212-16*(i)+8)
                    elseif blockIndex[blockID]["texture"][frameForAnim]~=nil then
                        gc:drawImage(_G[tostring(blockIndex[blockID]["texture"][frameForAnim])], ((i2-1)*16)-playStage.cameraOffset, 212-16*(i)+8) end
                else --load the object
                    plot2place(0,i2,i) --blank it out
                    objAPI:createObj(blockID,(i2-1)*16,212-16*(i),false)
    end end end end

    function playStage:drawBackground(gc) --rendered in rows from bottom to top w/ the rows from left to right
        for i=math.ceil(playStage.cameraOffset/16),math.ceil((318+playStage.cameraOffset)/16) do --left to right, horizontally, only draw what is visible on screen
            local THEME=plot2theme(i)
            if THEME==0 then --daytime
                gc:setColorRGB(97, 133, 248)
            elseif THEME==1 or THEME==2 then --underground or nighttime
                gc:setColorRGB(0, 0, 0)
            end
            gc:fillRect(((i-1)*16)-playStage.cameraOffset,0,18,212) --backdrop
    end end

    function playStage:levelLogic()
        if checkCollision(mouse.x,mouse.y,1,1,4,178,40,30) and playStage.EDITOR then
            cursor.set("hand pointer")
        else
            cursor.set("default")
        end
        playStage.framesPassedBlock=playStage.framesPassedBlock+1
        playStage.TIME=currentLevel.TIME-(math.floor(playStage.framesPassed/18))
        if playStage.TIME<=0 and not mario.dead then mario:kill() end
        --scrolling:
        if currentLevel.autoScroll then
            if playStage.cameraOffset>=((currentLevel.END-14)*16)-96 then
                playStage.cameraOffset=((currentLevel.END-14)*16)-96
            elseif not (playStage.wait or gui.PROMPT) and playStage.cameraOffset~=((currentLevel.END-14)*16)-96 then
                playStage.cameraOffset=playStage.cameraOffset+currentLevel.autoScroll
            end
        elseif mario.x>((currentLevel.END-14)*16) then -- if mario is at the end of the level
            playStage.cameraOffset=((currentLevel.END-14)*16)-96 --end of stage as declared by the level data
        elseif mario.x>96 then --if progressed past initial 96 pixels/6 blocks
            if not (currentLevel.disableBackScrolling==true and ((mario.x-96)<playStage.cameraOffset)) then
                playStage.cameraOffset=mario.x-96
            end
        elseif currentLevel.disableBackScrolling~=true then playStage.cameraOffset=0
        end
    end

    function playStage:objLogic()
        if not playStage.wait and not mario.powerUp then
            local spawnOffsets={(math.ceil(playStage.cameraOffset/16)*16)-48,((math.ceil(318+playStage.cameraOffset)/16)*16)+48} --view distance plus 3 blocks
            for list=1,#entityLists do --do all entity lists
                local focusedList=tostring(entityLists[list])
                for i=1,#_G[focusedList] do --for all entities within the list
                    if _G[tostring(_G[focusedList][i])]~=nil then --if entity exists
                        if (( _G[tostring(_G[focusedList][i])].y)>212) then
                            objAPI:destroy(_G[tostring(_G[focusedList][i])].classID,_G[tostring(_G[focusedList][i])].LEVEL)
                        elseif (((_G[tostring(_G[focusedList][i])].x) > (spawnOffsets[1])) and ((_G[tostring(_G[focusedList][i])].x) < (spawnOffsets[2]))) or ( _G[tostring(_G[focusedList][i])].GLOBAL==true) or currentLevel.enableGlobalEntities==true then --if in view distance
                            _G[tostring(_G[focusedList][i])]:logic()
                        elseif _G[tostring(_G[focusedList][i])].despawnable then
                            if _G[tostring(_G[focusedList][i])].x<-16 or (_G[tostring(_G[focusedList][i])].x < spawnOffsets[1]+8) or ((_G[tostring(_G[focusedList][i])].x) > spawnOffsets[2]-8) then
                                objAPI:destroy(_G[tostring(_G[focusedList][i])].classID,_G[tostring(_G[focusedList][i])].LEVEL) end
                        end
                    else table.remove(_G[focusedList],i) end --get rid of blank entities that may occur as a result of overloading, NOT a substantial issue
    end end end end

    function playStage:objDraw(gc,entityLists)
        local spawnOffsets={(math.ceil(playStage.cameraOffset/16)*16)-48,((math.ceil(318+playStage.cameraOffset)/16)*16)+48} --view distance plus 3 blocks
        for list=1,#entityLists do --do all entity lists
            local focusedList=tostring(entityLists[list])
            for i=1,#_G[focusedList] do
                if _G[tostring(_G[focusedList][i])]~=nil then
                    if (not ((_G[tostring(_G[focusedList][i])].x) < (spawnOffsets[1])) and not ((_G[tostring(_G[focusedList][i])].x) > (spawnOffsets[2]))) or _G[tostring(_G[focusedList][i])].GLOBAL==true then
                        _G[_G[focusedList][i]]:draw(gc)
    end end end end end

    function playStage:paint(gc) --all logic/drawing required to play the stage
        if playStage.load>1 then
        --logic
            if playStage.transition<=10 and not gui.PROMPT then
                if not playStage.wait then
                    playStage.framesPassed=playStage.framesPassed+1
                    objAPI:updatePlatforms()
                end
                playStage:levelLogic() --timer, scrolling etc
                playStage:handleInput() --receive information from keys pressed and parse it
                playStage:objLogic() --logic for every obj (powerups, enemies etc) except mario
                objAPI:cleanup() --transfers layers, destroys queued objects
                mario:logic()
            end
            
        --drawing (terrain and most objs)
            playStage:drawBackground(gc)
            playStage:objDraw(gc,{"entityListBackground"})
            playStage:drawTerrain(gc)
            playStage:objDraw(gc,{"entityListInner","entityListOuter"})
            mario:draw(gc)
            playStage:objDraw(gc,{"entityListParticle"})
        
        --hud (coins= %^&)
            local frameForAnim=(math.floor((framesPassed/4)%6))+1
            if frameForAnim<4 then frameForAnim="[" elseif frameForAnim==5 then frameForAnim="}" else frameForAnim="{" end
            local hud1=frameForAnim.."+"..addZeros(playStage.coinCount,2)
            local hud2=addZeros(playStage.SCORE,6).." > "..addZeros(playStage.TIME,3)
            if false then hud1="< +".."lives".." " end
            drawFont(gc,hud1,2,2,"left")
            drawFont(gc,hud2,316,2,"right")
            if playStage.TIME<=0 then drawFont(gc,"TIME UP",nil,nil,"centre") end
            if playStage.EDITOR then
                if checkCollision(mouse.x,mouse.y,1,1,4,178,40,30) then gc:drawImage(texture_button_create2,1,168)
                else gc:drawImage(texture_button_create1,6,178)
                end gc:drawImage(texture_prompt_enter,28,203)
            end

            gui:detectPos(0,8)

        --debug stuff
            if debug then --this is very messy and a complete clusterfuck
                local highlightedx=pixel2plot(mouse.x,mouse.y-8)[1]
                local highlightedy=pixel2plot(mouse.x,mouse.y-8)[2]
                local pixels = plot2pixel(highlightedx,highlightedy,true)
                gc:setColorRGB(255,255,100) 
                local ID=blockSelectionListTEMP[(blockSelectionTEMP%(#blockSelectionListTEMP))+1]
                local name=""
                if blockIndex[ID]~=nil then
                    name=(" ("..blockIndex[ID]["name"]..") ")
                end
                gc:drawString("select: "..ID..name.." velX: "..mario.vx.." velY: "..mario.vy, 0, 17, top)
                
                gc:drawString("("..(highlightedx-1)..": "..(13-highlightedy)..") despook: "..despook.." entities: "..#entityListOuter+#entityListParticle+#entityListInner+#entityListBackground, 0, 32, top)
                gc:drawString("blockX "..highlightedx.." blockY "..highlightedy.." id: "..getID(highlightedx,highlightedy).." x"..mouse.x.."y"..mouse.y-8, 0, 48, top)
                --gc:drawString("("..(highlightedx-1)..": "..(13-highlightedy)..") id: "..getID(highlightedx,highlightedy), 0, 48, top) --this is for translating GreatEd maps

                for i=1,#debugBoxes do
                    if debugBoxes[i][5]==true then --NOT global
                        gc:drawRect(debugBoxes[i][1],debugBoxes[i][2]+8,debugBoxes[i][3],debugBoxes[i][4])
                    else
                        gc:drawRect(debugBoxes[i][1]-playStage.cameraOffset,debugBoxes[i][2]+8,debugBoxes[i][3],debugBoxes[i][4])
                    end
                end
                debugBoxes={}
                timer2rainbow(gc,framesPassed+200,10)
                if ((framesPassed/15)%2) <= 1 then
                    gc:setPen("thin","dotted")
                else
                    gc:setPen("thin","dashed")
                end
                gc:drawRect(pixels[1],pixels[2]+8,15,15)
                gc:setPen("thin","smooth")
                gc:setColorRGB(255,255,100) 
            end

        --transition
            if playStage.transition>0 then
                playStage.transition=playStage.transition-1
                gc:setColorRGB(0,0,0)
                gc:fillRect(0,0,160-((20-playStage.transition)*8),212)
                gc:fillRect(160+(20-playStage.transition)*8,0,160-(20-playStage.transition)*8,212)
            end
        else
            gc:setColorRGB(0,0,0)
            gc:fillRect(0,0,318,212)
            if playStage.load==1 then
                if playStage.LEVELSTRING then
                    permLevel=string2level(playStage.LEVELSTRING)
                    playStage.LEVELSTRING=nil
                end
                currentLevel=copyLevel(permLevel)
                mario:resetPos()
                if type(playStage.EDITOR)=="table" then
                    mario.x=round(playStage.EDITOR[1]/16)*16
                    mario.y=round(playStage.EDITOR[2]/16)*16
                end
                playStage:levelLogic()
                mario:logic()
            end
            playStage.load=playStage.load+1
            drawFont(gc,"LOADING LEVEL FOR PLAY...", nil, nil,"centre",0)
        end
    end
