        editor.select2=false
        editor.platformSelect=false
        editor.eraseMode=false
        editor.eyedropperMode=false
        editor.playMode=false
        editor.minimised=false
        editor.mouseTile={}
        editor.mouseTile.x=0
        editor.mouseTile.y=0
        editor.highlightedArea=false
        editor.playTimer=false
        editor.displayedGroup=false
    end

    function toolpaletteSelection(group,option) --has to be a global function, because toolpalette reasons...
        if group=="Modifiers" then
            local modifier={"disableBackScrolling","allowBidirectionalSpawning","enableGlobalEntities","enableShellBouncing","enableCoinOnKill","enablePowerUpBouncing"} --possible modifiers, values that are nil when disabled and true when enabled
            for i=1,#modifier do
                print(string.sub(option,#option-#modifier[i]+1,#option),#modifier[i])
                if string.sub(option,#option-#modifier[i]+1,#option)==modifier[i] then
                    modifier=string.sub(option,#option-#modifier[i]+1,#option)
                    break
                end
            end
            if type(modifier)~="table" then --one has been located
                if permLevel[modifier]==nil then permLevel[modifier]=true
                else permLevel[modifier]=nil end
            end
        elseif group=="Autoscroll" then
            if string.sub(option,12,12)~=nil then
                permLevel.autoScroll=string.sub(option,12,12)
            else
                permLevel.autoScroll=nil
            end
        elseif group=="▶Automove" then
            if string.sub(option,-13)=="Walking Speed" then
                permLevel.autoMove="w"
            elseif string.sub(option,-13)=="Running Speed" then
                permLevel.autoMove="r"
            else permLevel.autoScroll=nil
            end
        elseif group=="⇥Length" then
            if string.sub(option,1,14)=="Current Length" then
                gui:createPrompt("SET LEVEL LENGTH",{"TYPE THE VALUE TO SET","THE LEVEL LENGTH TO!"},4,"length",false)
            else
                permLevel.END=permLevel.END+tonumber(option)
                if permLevel.END<=20 then permLevel.END=20 end
                permLevel=copyLevel(permLevel)
            end
        elseif group=="Time" then
            if string.sub(option,1,18)=="Current Time Limit" then
                gui:createPrompt("SET TIME LIMIT",{"TYPE THE VALUE TO SET","THE TIME LIMIT TO!"},3,"time",false)
            else
                permLevel.TIME=permLevel.TIME+tonumber(option)
                if permLevel.TIME<=5 then permLevel.TIME=5
                elseif permLevel.TIME>=999 then permLevel.TIME=999
            end end
        elseif group=="Save/Load" then
            if option=="Copy to Clipboard" then
                clipboard.addText(level2string(permLevel))
                gui:createPrompt("DONE!",{"LEVEL COPIED","TO CLIPBOARD!"},{{"OK","close"}},nil,nil,false)
            elseif option=="Load from Clipboard" then
                local PASTE=clipboard.getText() or "err"
                if string.sub(PASTE,1,1)=="<" then --very crude for now
                    editor:generate(PASTE)
                    gui:createPrompt("DONE!",{"LEVEL IMPORTED","FROM CLIPBOARD!"},{{"OK","close"}},nil,nil,false)
                else
                    gui:createPrompt("ERROR!",{"LEVEL IMPORTING","FAILED! CHECK TO","SEE IF THE LEVEL","CODE IS VALID."},{{"OK","close"}})
                end
            end
        end
        editor:updateToolpalette()
    end

    function editor:updateToolpalette(init)
        local dBS="[   ]" if permLevel.disableBackScrolling==true        then dBS="[✓]" end
        local aBS="[   ]" if permLevel.allowBidirectionalSpawning==true  then aBS="[✓]" end
        local eGE="[   ]" if permLevel.enableGlobalEntities==true        then eGE="[✓]" end
        local eSB="[   ]" if permLevel.enableShellBouncing==true         then eSB="[✓]" end
        local eCK="[   ]" if permLevel.enableCoinOnKill==true            then eCK="[✓]" end
        local ePB="[   ]" if permLevel.enablePowerUpBouncing==true       then ePB="[✓]" end

        local aSc={"[   ]","[   ]","[   ]","[   ]","[   ]","[   ]"} -- aSc: autoScroll
        if      permLevel.autoScroll==nil   then aSc[1]="[✓]"
        elseif  permLevel.autoScroll=="1"   then aSc[2]="[✓]"
        elseif  permLevel.autoScroll=="2"   then aSc[3]="[✓]"
        elseif  permLevel.autoScroll=="3"   then aSc[4]="[✓]"
        elseif  permLevel.autoScroll=="4"   then aSc[5]="[✓]"
        elseif  permLevel.autoScroll=="5"   then aSc[6]="[✓]"
        end

        local aMv={"[   ]","[   ]","[   ]"} -- aMv: autoMove
        if      permLevel.autoMove==nil then aMv[1]="[✓]"
        elseif  permLevel.autoMove=="w" then aMv[2]="[✓]"
        elseif  permLevel.autoMove=="r" then aMv[3]="[✓]"
        end

        local menu = {
            {"Modifiers",
                {dBS.."disableBackScrolling", toolpaletteSelection},
                {aBS.."allowBidirectionalSpawning", toolpaletteSelection},
