function mario:init()
    mario.velocity={}
    mario.pos={}
    self:resetPos()
end

function mario:logic()
    mario:calculateInput()
    mario:calculateMove()
    mario:calculateDirection()
end

function mario:calculateInput() --turns arrow inputs into velocity
--X movement
    if mario.power~=0 and (arrowDown==1) then
        mario.crouch=true
    elseif mario.power~=0 and mario.crouch and (pixel2plot(mario.pos.x+2-playStage.cameraOffset,mario.pos.y-8)[3]==1 or pixel2plot(mario.pos.x+13-playStage.cameraOffset,mario.pos.y-8)[3]==1) and mario.velocity.y==0 then
        mario.crouch=true 
    elseif mario.velocity.y==0 then mario.crouch=false end
    if mario.crouch then mario.velocity.x=mario.velocity.x*(0.93) end
    if (arrowLeft==1 or arrowRight==1) and mario.crouch and mario.velocity.y==0 then
        if arrowRight==1 then mario.velocity.x=arrowRight
        elseif arrowLeft==1 then mario.velocity.x=-arrowLeft end
    elseif arrowLeft==1 or arrowRight==1 then
        if arrowLeft==1 and mario.velocity.x>0.5 and mario.velocity.y==0 then mario.velocity.x=mario.velocity.x*(1-math.sin(mario.velocity.x/7)) --drifting slower
        elseif arrowRight==1 and mario.velocity.x<-0.5 and mario.velocity.y==0 then mario.velocity.x=mario.velocity.x*(1-math.sin(math.abs(mario.velocity.x)/7))
        else
        --max running speed 7
            if math.abs(mario.velocity.x)<2.0 then --walking under 2.0
                mario.velocity.x=mario.velocity.x+arrowRight*(math.random(3,5)/10)
                mario.velocity.x=mario.velocity.x-arrowLeft*(math.random(3,5)/10)
            elseif math.abs(mario.velocity.x)<4.5 then
                mario.velocity.x=mario.velocity.x+arrowRight*0.3
                mario.velocity.x=mario.velocity.x-arrowLeft*0.3
            elseif math.abs(mario.velocity.x)<7.01 and mario.velocity.y==0 then
                mario.velocity.x=mario.velocity.x+arrowRight*0.12
                mario.velocity.x=mario.velocity.x-arrowLeft*0.12
            elseif math.abs(mario.velocity.x)<7.01 and mario.velocity.y~=0 and ((arrowRight==1 and mario.velocity.x<-0.5) or (arrowLeft==1 and mario.velocity.x>0.5)) then
                mario.velocity.x=mario.velocity.x+arrowRight*0.6
                mario.velocity.x=mario.velocity.x-arrowLeft*0.6 end end
    elseif not mario.crouch then mario.velocity.x=mario.velocity.x*(0.8)
    end
    if mario.velocity.x>7 then mario.velocity.x=7 elseif mario.velocity.x<-7 then mario.velocity.x=-7 end 
    if math.abs(mario.velocity.x)<0.4 then mario.velocity.x=0 end --movement minumum, prevents velocity of 0.00001626 for example
--Y movement [TEMP CONTROL]
    if arrowUp==1 and not mario:gravityCheck(mario.pos.x,mario.pos.y,1) then  --up arrow pressed and on the floor (no double jumps)
        local runningBoost=0
        if math.abs(mario.velocity.x)>3 then runningBoost=math.abs(mario.velocity.x) end --momentum calculation
        mario.velocity.y=18+runningBoost --for a maximum of 25, jump ~5.5 blocks. without boost is 4 blocks (idle)
    else mario.velocity.y=mario.velocity.y*0.745 end --slow down upwards velocity when jumping (lower is floatier)
    if math.abs(mario.velocity.y)<0.6 then mario.velocity.y=0 end --movement minumum, prevents velocity of 0.00001626 for example
end

function mario:calculateMove() --use velocity to update position
    --X handling
    if mario:aggregateCheckX(mario.pos.x,mario.pos.y,mario.velocity.x,mario.power) then --no walls in X direction!
        mario.pos.x=mario.pos.x+mario.velocity.x --confirm movement
    else --wall detected
        mario.velocity.x=mario.velocity.x*0.5 --slow mario down so he can go closer to wall
        if mario:aggregateCheckX(mario.pos.x,mario.pos.y,mario.velocity.x,mario.power) then 
            mario.pos.x=mario.pos.x+mario.velocity.x
        end --prevent unsmooth stuttering near wall
    end
--Y handling
    if not mario:bumpCheck(mario.pos.x,mario.pos.y,-mario.velocity.y,mario.power) and mario.velocity.y>0 then --jump too large
        mario.velocity.y=mario.velocity.y*0.69 end -- 0.69 is 15.9/23... not a meme :< but yhyh this reduces jump size 
    if not mario:bumpCheck(mario.pos.x,mario.pos.y,0,mario.power) or not mario:bumpCheck(mario.pos.x,mario.pos.y,(-mario.velocity.y)/2,mario.power) then --this shit "fixes" it, idk why but dont touch this
        mario.velocity.y=-0.6 end
    if mario:gravityCheck(mario.pos.x,mario.pos.y,0) and not (mario.velocity.y>0.1) then --gravity occurs!
        local tempVelocity=0
        if mario:gravityCheck(mario.pos.x,mario.pos.y,8) then --large distance between floor
            if mario.velocity.y>-7 then -- terminal velocity: -7
                mario.velocity.y=mario.velocity.y-1.7
            else mario.velocity.y=-7 end
        else --small distance
            for i=-8,1 do
                if mario:gravityCheck(mario.pos.x,mario.pos.y,math.abs(i)) then
                    tempVelocity=i-1
                    mario.velocity.y=0
                    break end end end
        mario.pos.y=mario.pos.y-mario.velocity.y-tempVelocity
    elseif mario.velocity.y>0.1 then
        mario.pos.y=mario.pos.y-mario.velocity.y
    end 
    if not mario.crouch and not mario:gravityCheck(mario.pos.x,mario.pos.y,0) and mario.velocity.y==0 then --on ground, not jumping
    if arrowRight==1 then mario.dir="R"
    elseif arrowLeft==1 then mario.dir="L" end
    end
end

function mario:draw(gc)
    local drawOffset=0
    if mario.power~=0 then drawOffset=16 end
    gc:drawImage(_G[mario.dir..mario.power..mario.status],mario.pos.x-playStage.cameraOffset,mario.pos.y-drawOffset) --draw..mario
    gc:drawRect(mario.pos.x-playStage.cameraOffset+8,mario.pos.y-drawOffset+8,1,1)
end

function mario:calculateDirection()
    if not mario.crouch then
        if mario.velocity.y==0 then
            if mario.velocity.x~=0 then
                velocity2cycle=0
                if (math.abs(mario.velocity.x))>6.5 then velocity2cycle = 1.2 elseif (math.abs(mario.velocity.x))>4.5 then velocity2cycle = 0.6 elseif (math.abs(mario.velocity.x))>2 then velocity2cycle = 0.3 else velocity2cycle = 0.2 end
                mario.status="walk"..math.floor((velocity2cycle*framesPassed)%3)+1 end
            if arrowLeft==1 and mario.velocity.x>0.1 then mario.status="drift" --drift animation if arrow key is going opposite way to velocity
            elseif arrowRight==1 and mario.velocity.x<0.1 then mario.status="drift" end
            if mario.velocity.x==0 then mario.status="idle" end
            else mario.status="jump"
        end
    else mario.status="crouch"
    end
end

function mario:moveCheckX(xPos,yPos,xVel) --CHECKS FOR INTERFERENCE IN X AXIS returns true if nothing in the way
--X AXIS HANDLING
    --print(xPos,xVel)
    if xPos+xVel<0 then --edge of screen LEFT
        return false
    else --no edges of screen
        xPos=xPos+xVel --temp set to new pos
    end
    if pixel2plot(xPos+(xVel/16)-playStage.cameraOffset,yPos)[3]==1 then --check if new x pos in a wall
        return false
    else return true
    end
end
    
function mario:moveCheckY(xPos,yPos,yVel) --CHECKS FOR INTERFERENCE IN Y AXIS returns true if nothing in the way
--Y AXIS HANDLING
    if pixel2plot(xPos-playStage.cameraOffset,yPos+(yVel/16))[4] then --check if new y pos in a wall
        return false
    else return true
    end
end

function mario:aggregateCheckX(xPos,yPos,xVel,powerStatus) --checks points at head, feet, left, right
    if powerStatus==nil then powerStatus=0 end
    local topLeft=mario:moveCheckX(xPos+2,yPos+1,xVel) --more leniency
    local topRight=mario:moveCheckX(xPos+13,yPos+1,xVel) --more leniency, 14 pixel search
    local bottomLeft=mario:moveCheckX(xPos+2,yPos+15,xVel)
    local bottomRight=mario:moveCheckX(xPos+13,yPos+15,xVel)
    local powerLeft
    local powerRight----------------------------------------------------------------------------------------o_ <--powerLeft/Right, big mario calculated after (and hardcoded :<)
    if powerStatus~=0 and not mario.crouch then powerLeft=mario:moveCheckX(xPos+2,yPos-16+1,xVel) else powerLeft=true end --------O                   (offset also needed when rendering)
    if powerStatus~=0 and not mario.crouch then powerRight=mario:moveCheckX(xPos+13,yPos-16+1,xVel) else powerRight=true end-----@=@ <-- topLeft/Right, position always set as small mario height
    if topLeft and topRight and bottomLeft and bottomRight and powerLeft and powerRight then return true---/\  <--bottomLeft/Right
    else return false
    end
end

function mario:gravityCheck(xPos,yPos,yVel) --CHECKS IF STANDING ON BLOCK returns true if gravity applies
    local bottomLeft = mario:moveCheckY(xPos+2,yPos+16,16*yVel)
    local bottomRight = mario:moveCheckY(xPos+13,yPos+16,16*yVel)
    if bottomLeft and bottomRight then
        return true
    else return false
    end
end

function mario:bumpCheck(xPos,yPos,yVel,powerStatus) --CHECKS IF ABOUT TO COLLIDE WITH BLOCK returns true if bump applies
    if powerStatus==nil then powerStatus=0 end
    local topLeft = mario:moveCheckY(xPos+2,yPos,16*yVel) --easier to make tight jumps (has to be same as mario:gravityCheck or can climb walls) 
    local topRight = mario:moveCheckY(xPos+13,yPos,16*yVel)
    local powerLeft
    local powerRight
    if powerStatus>0 and not mario.crouch then powerLeft=mario:moveCheckY(xPos+2,yPos-15,16*yVel) powerRight=mario:moveCheckY(xPos+13,yPos-15,16*yVel)
    else powerLeft=true powerRight=true end
    if topLeft and topRight and powerLeft and powerRight then
        return true
    else return false
    end
end

function mario:resetPos()
    mario.dir="R"
    mario.crouch=false 
    mario.pos.x=16 mario.pos.y=20
    mario.velocity.x=0 mario.velocity.y=0
    mario.status="idle"
    mario.power=1
end

mario=mario()
