objShell=class()

    function objShell:init(classID,posX,posY,TYPE,velocity) --eg ("shell_g77215",64,64,"shell_g",-4)
        self.velocity={}
        if velocity==nil then self.velocity.x=0 else self.velocity.x=velocity end
