    function objAPI:checkShellCollision(classID,x,y,w,h,willBeKilled)
        classID=tostring(classID)
        for i=1,#playStage.shellBoundaries do --search for entry
            --table.insert(debugBoxes,{playStage.shellBoundaries[i][2],playStage.shellBoundaries[i][3],playStage.shellBoundaries[i][4],playStage.shellBoundaries[i][5]})
            --table.insert(debugBoxes,{x+2,y+2,w-4,h-4})
            if tostring(playStage.shellBoundaries[i][1])~=classID and (checkCollision(playStage.shellBoundaries[i][2],playStage.shellBoundaries[i][3],playStage.shellBoundaries[i][4],playStage.shellBoundaries[i][5],x+2,y+2,w-4,h-4)) then
                if willBeKilled==true then _G[tostring(playStage.shellBoundaries[i][1])]:handleStomp() end
