objBulletBill=class()

    function objBulletBill:init(classID,posX,posY,TYPE,fromBlaster) --eg ("bullet_L8173831",64,64,"bullet_L",true)
        self.TYPE=TYPE
        self.classID=classID
        self.pos={}
        self.pos.x=posX
        self.pos.y=posY
        self.status=1
        self.dead=false
        self.despawnable=true
        if self.TYPE=="bullet_L" then
            self.facing=-1
        else
            self.facing=1
        end
        if fromBlaster==true then
            self.LEVEL="entityListInner"
            self.timer=5
        else
            self.LEVEL="entityListOuter"
            self.timer=false
            objAPI:transferLayer(classID,"entityListInner","entityListOuter")
        end
    end

    function objBulletBill:logic() --handle both movement and animation
        if not self.dead then
    --CHECK IF MARIO COLLIDED
            local marioSize=16
            if mario.power==0 or mario.crouch then marioSize=0 end
            if checkCollision(mario.pos.x+1,mario.pos.y-marioSize+1,14,14+marioSize, self.pos.x+1,self.pos.y,14,1) and mario.velocity.y<0 then --hit on head
                self.dead=true
                self.status=3
                self.deathAnimTimer=framesPassed+10
                mario.velocity.tempY=15
                mario:handleStomp()
            elseif checkCollision(mario.pos.x+1,mario.pos.y-marioSize+1,14,14+marioSize, self.pos.x+1,self.pos.y+3,14,12) then --hit mario (side)
                mario:powerDownMario()
            end
    --X AXIS
            self.pos.x=self.pos.x+self.facing*3
        else
            self.pos.x=self.pos.x+self.facing*3
            self.pos.y=self.pos.y+5
        end
    --LAYER STUFF
        if self.timer then
            self.timer=self.timer-1
            if self.timer<0 then
                objAPI:transferLayer(self.classID,self.LEVEL,"entityListOuter")
                self.LEVEL="entityListOuter"
                self.timer=false
            end
        end
    end

    function objBulletBill:draw(gc)
        gc:drawImage(_G["texture_"..self.TYPE],self.pos.x-playStage.cameraOffset,self.pos.y+8)
    end


