objBlaster=class()

    function objBlaster:init(classID,posX,posY,TYPE) --possible types: blaster_L blaster_R blaster_LR
        self.classID=classID
        self.TYPE=TYPE
        self.LEVEL="entityListInner"
        self.pos={}
        self.pos.x=posX
        self.pos.y=posY
        self.despawnable=false
        self.timer=30
        local v=pixel2plot(self.pos.x,self.pos.y,true)
        playStage.currentLevel["x"..(v[1]+1).."y"..v[2]]=99 --make block solid
    end

    function objBlaster:logic()
        self.timer=self.timer-1
        if self.timer<0 then
            local marioDistance=math.abs(mario.pos.x-self.pos.x)
            if marioDistance>47 then
                if mario.pos.x<self.pos.x and (self.TYPE=="blaster_L" or self.TYPE=="blaster_LR") and pixel2solid(self.pos.x-8,self.pos.y+8,true)==false then --shoot left
                    objAPI:createObj("bullet_L",self.pos.x,self.pos.y,nil,true)
                    objAPI:sendToFront(self.classID,self.LEVEL)
                    self.timer=60
                elseif mario.pos.x>self.pos.x and (self.TYPE=="blaster_R" or self.TYPE=="blaster_LR") and pixel2solid(self.pos.x+20,self.pos.y+8,true)==false then --shoot right
                    self.timer=60
                    objAPI:createObj("bullet_R",self.pos.x,self.pos.y,nil,true)
                    objAPI:sendToFront(self.classID,self.LEVEL)
                end
            end
