objShell=class()

    function objShell:init(classID,posX,posY,TYPE,velocity,fromKoopa) --eg ("shell_g77215",64,64,"shell_g",-4)
        self.velocity={}
        if velocity==nil or velocity==0 then 
            self.velocity.x=0 
            if fromKoopa==true then self.koopaTimer=framesPassed+200 else self.koopaTimer=false end
        else self.velocity.x=velocity self.koopaTimer=false end
        self.velocity.y=0
        self.LEVEL="entityListInner"
        self.TYPE=TYPE
        self.classID=classID
        self.pos={}
        self.fromKoopa=fromKoopa
        self.pos.x=posX
        self.pos.y=posY
        self.status=1
        self.dead=false
        self.hitTimer=0
        self.despawnable=false
        self.hitCount=0
    end
    function objShell:logic() --handle both movement and animation
        if self.pos.y>212 then
            self.dead=true
        end
        if not self.dead then
    --CHECK IF MARIO COLLIDED
            local marioSize=16
            if mario.power==0 or mario.crouch then marioSize=0 end            
            if checkCollision(mario.pos.x+1,mario.pos.y-marioSize+1,14,14+marioSize, self.pos.x+1,self.pos.y,14,1) and mario.velocity.y<0 and (self.hitTimer<framesPassed) then --hit from above
                self.hitTimer=framesPassed+6
                mario:handleStomp()
                self.hitCount=0 self.hitCountSTOR=0
                local shakeCondition=true
                if self.koopaTimer~=false then if (self.koopaTimer-45<framesPassed) then shakeCondition=false end end
                if self.velocity.x==0 and shakeCondition then
                    self.koopaTimer=false
                    mario.velocity.tempY=8
                    if self.pos.x>mario.pos.x then self.velocity.x=4 else self.velocity.x=-4 end
                else self.velocity.x=0 objAPI:removeShellBoundaries(self.classID) self.koopaTimer=framesPassed+200 mario.velocity.tempY=15 end
            elseif checkCollision(mario.pos.x+1,mario.pos.y-marioSize+1,14,14+marioSize, self.pos.x+1,self.pos.y+3,14,12) and (self.hitTimer<framesPassed) then --hit mario from side
                self.hitTimer=framesPassed+6
                if self.velocity.x==0 then
                    self.koopaTimer=false
                    objAPI:addStats("points",400,self.pos.x,self.pos.y)
                    self.hitCount=0 self.hitCountSTOR=0
                    if self.pos.x>mario.pos.x then self.velocity.x=8 else self.velocity.x=-8 end
                else
                    mario:powerDownMario() end
            end
    --CHECK IF HIT (AND LATER FIREBALL)
            if bumpedBoundary ~= false then
                if checkCollision(self.pos.x+2,self.pos.y+14,12,3,bumpedBoundary[1],bumpedBoundary[2],bumpedBoundary[3],bumpedBoundary[4]) then
                    self.velocity.y=-11
                    self.dead=true
                    self.koopaTimer=false
                    self.status=3
                    objAPI:removeShellBoundaries(self.classID)
                end
            end
            if objAPI:checkShellCollision(self.classID,self.pos.x,self.pos.y,16,16,true) then
                self.velocity.y=-11
                self.dead=true
                self.status=3
                self.koopaTimer=false
                objAPI:removeShellBoundaries(self.classID)
            end
    --UPDATE SHELL BOUNDARY
            if self.velocity.x~=0 then
                objAPI:updateShellBoundaries(self.classID,self.pos.x,self.pos.y) end
    --X AXIS
            if objAPI:aggregateCheckX(false,self.pos.x,self.pos.y,self.velocity.x,0,true) then
                self.pos.x=self.pos.x+(self.velocity.x)
            else
                local offset=0
                if self.velocity.x>0 then offset=16 end
                if pixel2bumpable(self.pos.x+self.velocity.x+offset,self.pos.y+15,true) then
                    local v=pixel2plot(self.pos.x+self.velocity.x+offset,self.pos.y+15,true)
                    objAPI:handleBumpedBlock(v[1],v[2],true) end
                self.velocity.x=-self.velocity.x
                if objAPI:aggregateCheckX(false,self.pos.x,self.pos.y,self.velocity.x,0,true) then
                    self.pos.x=self.pos.x+(self.velocity.x)
                elseif not objAPI:aggregateCheckX(false,self.pos.x,self.pos.y,0,0,true) then
                    objAPI:destroy(self.classID,self.LEVEL) end
            end
            if not objAPI:aggregateCheckX(false,self.pos.x,self.pos.y,0,0,true) then --stuck in a block
                objAPI:destroy(self.classID,self.LEVEL) objAPI:removeShellBoundaries(self.classID) end
    --Y AXIS
            if self.velocity.y>0.1 then
                if objAPI:bumpCheck(false,self.pos.x,self.pos.y,self.velocity.y) then
                    self.pos.y=self.pos.y-self.velocity.y
                    self.velocity.y=self.velocity.y*0.7
                else 
                    self.velocity.y=0
                end
                if self.velocity.y<0.5 then
                    self.velocity.y=0
                end
            elseif objAPI:gravityCheck(false,self.pos.x,self.pos.y,0) and objAPI:aggregateCheckX(false,self.pos.x,self.pos.y,0) then --gravity
                if objAPI:gravityCheck(false,self.pos.x,self.pos.y,1) then
                    if objAPI:gravityCheck(false,self.pos.x,self.pos.y,5) then
                        self.pos.y=self.pos.y+5
                    else
                        self.pos.y=self.pos.y+1 end
                else
                    self.pos.y=self.pos.y+1 end
            end
            if self.velocity.x~=0 then
                objAPI:coinCheck(self.pos.x,self.pos.y,0)
            end
    --ANIMATION
            if not self.dead then
                if self.koopaTimer==false then
                    self.status=1
                elseif self.fromKoopa then
                    if self.koopaTimer<framesPassed then
                        objAPI:createObj("koopa"..string.sub(self.TYPE,6,8),self.pos.x,self.pos.y)
                        objAPI:removeShellBoundaries(self.classID)
                        objAPI:destroy(self.classID,self.LEVEL)
                        self.status=0
                    elseif (self.koopaTimer-7<framesPassed) then
                        self.status=2
                    elseif (self.koopaTimer-45<framesPassed) then
                        local animOption=((math.ceil((framesPassed)))%4)
                        if animOption==0 or animOption==2 then self.status=1
                        elseif animOption==1 then self.status=4
                        else self.status=5 end
                    else
                        self.status=1
                    end
                else
                    self.koopaTimer=false
                end
            end
        elseif self.dead then
            objAPI:removeShellBoundaries(self.classID)
            if self.status==3 then
                if self.velocity.x==0 then self.velocity.x=1 end
                self.pos.x=self.pos.x+(2*(self.velocity.x/self.velocity.x))
                if self.velocity.y<-0.5 then
                    self.pos.y=self.pos.y+self.velocity.y
                    self.velocity.y=(self.velocity.y+0.2)*0.8
                elseif (self.velocity.y<0 and self.velocity.y>-0.5) or self.velocity.y>0 then
                    self.velocity.y=(math.abs(self.velocity.y)+0.3)*1.09
                    self.pos.y=self.pos.y+self.velocity.y
                end
            end
        end
    end
    function objShell:handleStomp()
        self.hitCount=self.hitCount+1
        if self.hitCount~=0 then
            if self.hitCount<(#hitProgressionKoopa+1) then
                objAPI:addStats("points",hitProgressionKoopa[self.hitCount],self.pos.x,self.pos.y+16)
            else
                objAPI:addStats("1up",1,self.pos.x,self.pos.y+16)
            end
        end
    end
    function objShell:draw(gc)
        local offsetY=0
        if self.status~=0 then
            if not studentSoftware and (self.status==4 or self.status==5) then offsetY=-2 end
            gc:drawImage(_G["texture_"..self.TYPE.."_"..self.status],self.pos.x-playStage.cameraOffset,self.pos.y+8+offsetY) end --"texture_shell_R_1"
    end

