        local v={pixel2plot(xPos-playStage.cameraOffset,yPos+(1))[1],pixel2plot(xPos-playStage.cameraOffset,yPos+(1))[2]}
        v = plot2pixel(v[1],v[2],true)[2]+16
        return {v[1],v[2]-12}
    end]]

    function level2string(levelData)
        local starts=pixel2plot(levelData.startX,levelData.startY,true)
        local STRING="<"..levelData.END.."-".."v2".."-"..starts[1].."~"..starts[2]..">" --header: end of stage, version of string conversion (for compatibility)
        for x=1,levelData.END do -- x axis, left to right, horizontally per row
            for y=1,13 do -- y axis, bottom to top, in a column
                STRING=STRING..","..(levelData["x"..x.."y"..y])
            end
        end
        return STRING
    end

    function string2level(STRING)
        local function splitByChar(input,char)
            local output={}
            for str in string.gmatch(input, "([^"..char.."]+)") do
                table.insert(output, str)
            end return output
        end
        local levelDataTable={}
        local levelData={}
        levelDataTable=splitByChar(STRING,",")
        local HEADER=string.sub(levelDataTable[1],2,(#levelDataTable[1]-1))
        HEADER=splitByChar(HEADER,"-")
        levelData.END=HEADER[1]
        levelData.version=HEADER[2]
        local starts=splitByChar(HEADER[3],"~")
        starts=plot2pixel(starts[1],starts[2])
        levelData.startX=starts[1]+1 levelData.startY=starts[2]
        local tablePos=1
        for x=1,levelData.END do
            for y=1,13 do
                tablePos=tablePos+1
                if isInteger(levelDataTable[tablePos]) then
                    levelData["x"..x.."y"..y]=tonumber(levelDataTable[tablePos])
                else levelData["x"..x.."y"..y]=tostring(levelDataTable[tablePos]) end
            end
        end
        return (levelData)
    end

    function copyLevel(levelData)
        return string2level(level2string(levelData))
    end

    function drawFont(gc,text,x,y,position,spacing)
        local function isAlphaNumeric(input)
            return not (input:match("%W"))
        end
        local drawOffset=0
        if x==nil then x=158 end
        if y==nil then y=106 end
        if spacing==nil then spacing=0 end
        if position~=nil then
            local totalLength=(#text*(8+spacing))-1
            if position=="left" then drawOffset=0
            elseif position=="centre" or position=="center" then drawOffset=-math.ceil(totalLength/2) --british english and american english #_#
            elseif position=="right" then drawOffset=-totalLength
            end
        end
        for i=1,#text do
            local letter=string.sub(text,i,i)
            if isAlphaNumeric(letter) then
                gc:drawImage(_G["texture_font_"..letter],((i-1)*(8+spacing))+x+drawOffset,y)
            elseif fontLookup[letter]~=nil then
                gc:drawImage(_G["texture_font_"..fontLookup[letter]],((i-1)*(8+spacing))+x+drawOffset,y)
            end
        end
    end

