mario=class()

    function mario:init()
        mario.velocity={}
        mario.pos={}
        mario:resetPos()
    end

    function mario:resetPos() --default config
        mario.powerUp=false
        mario.powerDown=false
        mario.dead=false
        mario.powerAnimTimer=0
        mario.dir="R"
        mario.crouch=false 
        mario.pos.x=playStage.currentLevel.startX mario.pos.y=playStage.currentLevel.startY
        mario.velocity.x=0 mario.velocity.y=0
        mario.status="idle"
        mario.power=0
        playStage.wait=false
        mario.iFrames=-1
        mario.hitCount=0
        mario.starAnim=false
        mario.actionAnimTimer=0
        mario.starTimer=0
    end

    function mario:logic() --direction is seperate as its still needed during pause
        mario.starAnim=false
        if not playStage.wait and not mario.powerUp and not mario.powerDown then
            mario:calculateInput()
            mario:calculateMove()
            objAPI:coinCheck(mario.pos.x,mario.pos.y,mario.power)
        end
        mario:calculateAnim()
    end

    function mario:calculateInput() --turns inputs into velocity (or crouch/fireball)
    --X movement
        if mario.power~=0 and (input.down==1) and mario.velocity.y==0 then
            mario.crouch=true
            mario.actionAnimTimer=0
        elseif mario.power~=0 and ((pixel2solid(mario.pos.x+2-playStage.cameraOffset,mario.pos.y-8) and not pixel2anything("marioonly",mario.pos.x+2-playStage.cameraOffset,mario.pos.y-8)) or (pixel2solid(mario.pos.x+13-playStage.cameraOffset,mario.pos.y-8) and not pixel2anything("marioonly",mario.pos.x+13-playStage.cameraOffset,mario.pos.y-8))) and mario.velocity.y==0 then
            mario.crouch=true
            mario.actionAnimTimer=0
        elseif mario.velocity.y==0 or mario.power==0 then mario.crouch=false end
        if mario.crouch then mario.velocity.x=mario.velocity.x*(0.93) end
        if (input.left==1 or input.right==1) and mario.crouch and mario.velocity.y==0 and (pixel2solid(mario.pos.x+2-playStage.cameraOffset,mario.pos.y-8) or pixel2solid(mario.pos.x+13-playStage.cameraOffset,mario.pos.y-8)) then
            if input.right==1 then mario.velocity.x=input.right
            elseif input.left==1 then mario.velocity.x=-input.left end
        elseif (input.left==1 or input.right==1) and (not mario.crouch or mario.velocity.y~=0) then
            if ((input.left==1 and mario.velocity.x>0.5) or (input.right==1 and mario.velocity.x<-0.5)) and mario.velocity.y==0 then mario.velocity.x=mario.velocity.x*0.9 --drifting slower
            else --not drifting
            --max running speed 7
                if math.abs(mario.velocity.x)<2.0 then --walking under 2.0
                    mario.velocity.x=mario.velocity.x+input.right*(math.random(3,5)/10)
                    mario.velocity.x=mario.velocity.x-input.left*(math.random(3,5)/10)
                elseif math.abs(mario.velocity.x)<4.5 then
                    mario.velocity.x=mario.velocity.x+input.right*0.3
                    mario.velocity.x=mario.velocity.x-input.left*0.3
                elseif math.abs(mario.velocity.x)<=7 and mario.velocity.y==0 then
                    mario.velocity.x=mario.velocity.x+input.right*0.12
                    mario.velocity.x=mario.velocity.x-input.left*0.12
                elseif math.abs(mario.velocity.x)<=7 and mario.velocity.y~=0 and ((input.right==1 and mario.velocity.x<-0.5) or (input.left==1 and mario.velocity.x>0.5)) then
                    mario.velocity.x=mario.velocity.x+input.right*0.6
                    mario.velocity.x=mario.velocity.x-input.left*0.6 end end
        elseif not mario.crouch then mario.velocity.x=mario.velocity.x*(0.8)
        end
        if mario.velocity.x>=7 then mario.velocity.x=7 elseif mario.velocity.x<=-7 then mario.velocity.x=-7 end 
        if math.abs(mario.velocity.x)<0.4 then mario.velocity.x=0 end --movement minumum, prevents velocity of 0.00001626 for example
    --Y movement
        if input.up==1 and not objAPI:gravityCheck(true,mario.pos.x,mario.pos.y,0) then  --up arrow pressed and on the floor (no double jumps)
            local runningBoost=0
            if math.abs(mario.velocity.x)>3 then runningBoost=math.abs(mario.velocity.x) end --momentum calculation
            mario.velocity.y=18+runningBoost --for a maximum of 25, jump ~5.5 blocks. without boost is 4 blocks (idle)
        else mario.velocity.y=mario.velocity.y*0.745 end --slow down upwards velocity when jumping (lower is floatier)
        if math.abs(mario.velocity.y)<0.6 then mario.velocity.y=0 end --movement minumum, prevents velocity of 0.00001626 for example
    --SPECIAL ACTIONS
        if input.action==1 and mario.power==2 and not mario.crouch then
            local fireballCount=0
            for i=1,#playStage.entityListParticle do
                if string.sub(playStage.entityListParticle[1],1,8)=="fireball" then
                    fireballCount=fireballCount+1
                end
            end
            if fireballCount<2 then
                mario.actionAnimTimer=2
                if mario.dir=="L" then
                    objAPI:createObj("fireball_L",mario.pos.x,mario.pos.y)
                else
                    objAPI:createObj("fireball_R",mario.pos.x+16,mario.pos.y)
                end
            end
        end
    end

    function mario:calculateMove() --use velocity to update position
        if mario.velocity.tempY ~= nil then
            mario.velocity.y=mario.velocity.tempY
            mario.velocity.tempY=nil
        end
        mario.velocity.x=round(mario.velocity.x,2)
        mario.velocity.y=round(mario.velocity.y,2)
    --X handling
        if objAPI:aggregateCheckX(true,mario.pos.x,mario.pos.y,mario.velocity.x,mario.power) then --no walls in X direction!
            mario.pos.x=mario.pos.x+mario.velocity.x --confirm movement
        else --wall detected
            while not objAPI:aggregateCheckX(true,mario.pos.x,mario.pos.y,mario.velocity.x,mario.power) and math.abs(mario.velocity.x)>0.5 do
                mario.velocity.x=mario.velocity.x*0.9 end --slow mario down so he can go closer to wall
            if math.abs(mario.velocity.x)<0.9 then mario.velocity.x=0 end
                mario.pos.x=mario.pos.x+mario.velocity.x
        end
    --Y handling
        if not objAPI:bumpCheck(true,mario.pos.x,mario.pos.y,mario.velocity.y,mario.power) and mario.velocity.y>0 then --jump too large
            for i=math.floor(mario.velocity.y),0,-1 do
                if objAPI:bumpCheck(true,mario.pos.x,mario.pos.y,i,mario.power) then
                    mario.velocity.y=i+1 break end end end
        if not objAPI:bumpCheck(true,mario.pos.x,mario.pos.y,0,mario.power) or not objAPI:bumpCheck(true,mario.pos.x,mario.pos.y,(mario.velocity.y)/2,mario.power) then --this shit "fixes" it, idk why but dont touch this
            local bigOffset=0
            if mario.power>0 and not mario.crouch then bigOffset=16 end
            if pixel2bumpable(mario.pos.x+2-playStage.cameraOffset,mario.pos.y-bigOffset-8) or pixel2bumpable(mario.pos.x+13-playStage.cameraOffset,mario.pos.y-bigOffset-8) and mario.velocity.y>0 then --something can be bumped here
                local xOffset
                if pixel2bumpable(mario.pos.x+8-playStage.cameraOffset,mario.pos.y-bigOffset-8) then --prioritise whatever is in the middle
                    xOffset=8
                elseif pixel2bumpable(mario.pos.x+2-playStage.cameraOffset,mario.pos.y-bigOffset-8) then
                    xOffset=2
                else
                    xOffset=13
                end
                local LOCs=pixel2plot(mario.pos.x+xOffset-playStage.cameraOffset,mario.pos.y-bigOffset-8)
                local xLOC=LOCs[1] local yLOC=LOCs[2]
                objAPI:handleBumpedBlock(xLOC,yLOC)
            end
            mario.velocity.y=-0.6
        end
        
        if objAPI:gravityCheck(true,mario.pos.x,mario.pos.y,0) and (mario.velocity.y<0.1) then --gravity occurs!
            local tempVelocity=0
            if objAPI:gravityCheck(true,mario.pos.x,mario.pos.y,8) then --large distance between floor
                if mario.velocity.y>-7 then -- terminal velocity: -7
                    mario.velocity.y=mario.velocity.y-1.7
                else mario.velocity.y=-7 end
            elseif objAPI:gravityCheck(true,mario.pos.x,mario.pos.y,mario.velocity.y) then --small distance
                for i=-7,-1 do
                    if objAPI:gravityCheck(true,mario.pos.x,mario.pos.y,-i) then
                        tempVelocity=i-1
                        mario.velocity.y=0
                        break end end end
            mario.pos.y=mario.pos.y-mario.velocity.y-tempVelocity
        elseif mario.velocity.y>0.1 then
            mario.pos.y=mario.pos.y-mario.velocity.y
        else
            mario.hitCount=0
            mario.velocity.y=0
        end
    --OTHER (death plane)
        if mario.pos.y>216 then
            mario:kill()
        end
    end

    function mario:calculateAnim(calculateAnimForce) --handles mario's visuals (walk cycles, animations etc)
        if (not mario.powerUp and not mario.powerDown and not playStage.wait and not mario.dead) or calculateAnimForce then --normal gameplay
            mario.powerAnim=mario.power
            if not mario.crouch then
                if mario.velocity.y==0 then
                    if mario.velocity.x~=0 then
                        local velocity2cycle=0
                        if (math.abs(mario.velocity.x))>6.5 then velocity2cycle = 1.2 elseif (math.abs(mario.velocity.x))>4.5 then velocity2cycle = 0.6 elseif (math.abs(mario.velocity.x))>2 then velocity2cycle = 0.3 else velocity2cycle = 0.2 end
                        mario.status="walk"..math.floor((velocity2cycle*playStage.framesPassed)%3)+1 end
                    if input.left==1 and mario.velocity.x>0.01 then mario.status="drift" input.right=0 --drift animation if arrow key is going opposite way to velocity
                    elseif input.right==1 and mario.velocity.x<-0.01 then mario.status="drift" input.left=0 end
                    if mario.velocity.x==0 then mario.status="idle" end
                    else mario.status="jump"
                end
            else
                mario.status="crouch"
            end
            if mario.actionAnimTimer>0 then
                mario.actionAnimTimer=mario.actionAnimTimer-1
                mario.status="fire"
            end
        elseif mario.powerUp and playStage.wait then --powering UP in progress
            if mario.power==1 then --growing to big mario
                if playStage.framesPassedBlock-12<mario.powerAnimTimer then 
                    local animOption=(math.ceil((playStage.framesPassedBlock/2)))%2
                    if animOption==1 then mario.powerAnim=1 mario.status="grow" else mario.powerAnim=0 mario.status="idle" end
                else
                    local animOption=(math.ceil((playStage.framesPassedBlock/2)))%3
                    if animOption==0 then mario.powerAnim=0 mario.status="idle" elseif animOption==1 then mario.powerAnim=1 mario.status="grow" else mario.powerAnim=1 mario.status="idle" end
                end
            elseif mario.power==2 then --growing to fire mario
                local animOption=(math.ceil((playStage.framesPassedBlock/2)))%4
                mario.powerAnim=2
                mario.starAnim=2
            end
            if playStage.framesPassedBlock-24>mario.powerAnimTimer then --end animation
                mario.powerUp=false
                playStage.wait=false
                mario.starAnim=false
            end
        elseif mario.powerDown and playStage.wait then --powering DOWN in progress
            if mario.power==0 then
                local animOption=(math.ceil((playStage.framesPassedBlock/flashingDelay)))%2
                if playStage.framesPassedBlock-12<mario.powerAnimTimer then --powering down to small mario
                    if animOption==1 then mario.powerAnim=mario.power+1 mario.status=mario.animCache else mario.powerAnim=mario.power+1 mario.status="invisible" end --flash
                else --down to big
                    if mario.animCache=="crouch" then
                        mario.animCache="walk1" end --as crouch does not exist for small mario
                    if animOption==1 then mario.powerAnim=mario.power mario.status=mario.animCache else mario.powerAnim=mario.power mario.status="invisible" end --flash
                end
            elseif mario.power==1 then --powering down to big mario
                local animOption=(math.ceil((playStage.framesPassedBlock/3)))%2
                if animOption==1 then
                    mario.powerAnim=1
                else
                    mario.powerAnim=2
                end
            end
            if playStage.framesPassedBlock-24>mario.powerAnimTimer then --end power down anim
                mario.powerDown=false
                playStage.wait=false
                mario.iFrames=playStage.framesPassed+30
            end
        elseif mario.dead then --mario death animation
            mario.powerAnim=0
            if mario.velocity.death<-0.5 and (playStage.framesPassedBlock>mario.deathAnimTimer+12) then
                mario.pos.y=mario.pos.y+mario.velocity.death
                mario.velocity.death=(mario.velocity.death+0.2)*0.8
            elseif (mario.velocity.death<0 and mario.velocity.death>-0.5) or mario.velocity.death>0 then
                mario.velocity.death=(math.abs(mario.velocity.death)+0.3)*1.09
                mario.pos.y=mario.pos.y+mario.velocity.death
            end
            if mario.pos.y>220 then
                if not mario.respawnTime then
                    mario.respawnTime=playStage.framesPassedBlock+18
                end
                if playStage.framesPassedBlock>mario.respawnTime then
                    on.escapeKey()
                end
            end
        end
        if (mario.starTimer>playStage.framesPassed) then --handle star anim and hitbox
            if not playStage.wait then
                if (mario.starTimer-70>playStage.framesPassed) then
                    mario.starAnim=2
                elseif (mario.starTimer-15>playStage.framesPassed) then
                    mario.starAnim=5 --slow down anim
                end
            end
            local h=0
            if mario.power>0 then
                h=16
            end
            objAPI:addHitBox("mario",mario.pos.x,mario.pos.y-h,16,16+h,"mario")
        end
        if not playStage.wait and not mario.powerUp and not mario.powerDown and not (mario.crouch and (pixel2ID(mario.pos.x+2-playStage.cameraOffset,mario.pos.y-8)==1 or pixel2ID(mario.pos.x+13-playStage.cameraOffset,mario.pos.y-8)==1)) and not objAPI:gravityCheck(true,mario.pos.x,mario.pos.y,0) and mario.velocity.y==0 then --on ground, not jumping
            if input.right==1 then mario.dir="R"
            elseif input.left==1 then mario.dir="L" end
        end
    end

    function mario:powerUpMario(optionalPower,forced)
        if not mario.dead and not mario.powerUp and not mario.powerDown then
            local proceed=true
            if optionalPower~=nil and (forced==true or optionalPower>mario.power) then
                mario.power=optionalPower
            elseif optionalPower==nil and mario.power<2 then mario.power=mario.power+1
            else proceed=false end --there is nothing to power to
            if proceed then
                playStage.wait=true
                mario.powerUp=true
                mario.powerDown=false
                mario.powerAnimTimer=playStage.framesPassedBlock
                mario.iFrames=0
                mario:calculateAnim(true)
            end
        end
    end

    function mario:powerDownMario(optionalPower)
        if mario.power>0 and not mario.dead and mario.powerDown==false and not mario.powerUp and not (mario.iFrames>playStage.framesPassed) then mario.power=mario.power-1
            playStage.wait=true
            mario.powerDown=true
            mario.powerUp=false
            mario.powerAnimTimer=playStage.framesPassedBlock
            mario.animCache=mario.status
            mario.iFrames=0
        elseif not mario.dead and not (mario.iFrames>playStage.framesPassed) and mario.powerDown==false and mario.power==0 then
            mario.kill()
        end
    end

    function mario:powerStarMario(optionalLength)
        if optionalLength==nil then
            mario.starTimer=playStage.framesPassed+200
        else
            mario.starTimer=playStage.framesPassed+optionalLength
        end
    end

    function mario:kill()
        mario.velocity.death=-11
        mario.respawnTime=false
        mario.status="death"
        playStage.wait=true
        mario.powerDown=false
        mario.powerUp=false
        mario.dead=true
        mario.power=0
        mario.deathAnimTimer=playStage.framesPassedBlock
    end

    function mario:draw(gc)
        local drawOffset=0
        local star=""
        if mario.powerAnim~=0 then drawOffset=16 end
        if mario.starAnim~=false then
            local animOption=(math.ceil((playStage.framesPassedBlock/mario.starAnim)))%4
            if animOption~=3 then
                if mario.powerAnim>0 then
                    mario.powerAnim=2
                else
                    mario.powerAnim=0
                end
                star="star"..animOption
            end
        end
        if mario.iFrames>playStage.framesPassed and not playStage.wait then --currently under influence of iframes
            local animOption=(math.ceil((playStage.framesPassed/flashingDelay)))%2
            if animOption==1 then mario.status="invisible" end
        end
        if mario.status~="invisible" then
            gc:drawImage(_G[mario.dir..mario.powerAnim..mario.status..star],mario.pos.x-playStage.cameraOffset,mario.pos.y-drawOffset+8) --draw..mario
        end
    end

    function mario:handleStomp()
        mario.hitCount=mario.hitCount+1
        if mario.hitCount~=0 then
            if mario.hitCount<#hitProgressionMario+1 then
                objAPI:addStats("points",hitProgressionMario[mario.hitCount],mario.pos.x,mario.pos.y-16)
            else --1up
                objAPI:addStats("1up",1,mario.pos.x,mario.pos.y-16)
            end
        end
    end


