objGoomba=class()

    function objGoomba:init(classID,posX,posY,TYPE) --eg ("goomba77215",64,64,"goomba")
        self.velocity={}
        self.velocity.y=0
        self.LEVEL="entityListInner"
        self.TYPE=TYPE
        self.classID=classID
        self.pos={}
        self.pos.x=posX
        self.pos.y=posY
        self.status=1
        self.dead=false
        self.despawnable=false --for now, unless pipe spawning added
        self.hitBox={16,16,true,true}
        if mario.pos.x>self.pos.x then
            self.dir=1 -- -1 is left, 1 is right
        else self.dir=-1 end
    end

    function objGoomba:logic() --handle both movement and animation
        if not objAPI:aggregateCheckX(true,self.pos.x,self.pos.y,0,0,true) and not self.status==4 then --stuck in a block
            objAPI:destroy(self.classID,self.LEVEL) return end
        if not self.dead then
    --CHECK IF MARIO COLLIDED
            if not (mario.starTimer>playStage.framesPassed) then
                local marioSize=16
                if mario.power==0 or mario.crouch then marioSize=0 end
                if checkCollision(mario.pos.x+1,mario.pos.y-marioSize+1,14,14+marioSize, self.pos.x+1,self.pos.y,14,1) and mario.velocity.y<0 then --hit on head
                    self.dead=true
                    self.status=3
                    self.deathAnimTimer=playStage.framesPassed+10
                    mario.velocity.tempY=15
                    mario:handleStomp()
                    objAPI:sendToFront(self.classID,self.LEVEL)
                elseif checkCollision(mario.pos.x+1,mario.pos.y-marioSize+1,14,14+marioSize, self.pos.x+1,self.pos.y+3,14,12) then --hit mario (side)
                    mario:powerDownMario()
                end
            end
    --X AXIS
            if objAPI:aggregateCheckX(false,self.pos.x,self.pos.y,self.dir*2,0,true) then
                self.pos.x=self.pos.x+(self.dir*2)
            else
                self.dir=-self.dir
                if objAPI:aggregateCheckX(false,self.pos.x,self.pos.y,self.dir*2,0,true) then
                    self.pos.x=self.pos.x+(self.dir*2)
                elseif not objAPI:aggregateCheckX(false,self.pos.x,self.pos.y,0,0,true) then
                    objAPI:destroy(self.classID,self.LEVEL) end
            end
    --Y AXIS
            if self.velocity.y>0 then --ascending
                if self.velocity.y>0.5 then --significant velocity
                    self.velocity.y=self.velocity.y*0.7
                else --small velocity
                    self.velocity.y=-0.5
                end
            elseif self.velocity.y<0 then --descending
                if self.velocity.y>-6 then --significant velocity
                    self.velocity.y=self.velocity.y*1.5
                end
                if self.velocity.y<-6 then
                    self.velocity.y=-6
                    self.pos.y=math.floor(self.pos.y/6)*6
                end
            end
            if self.velocity.y<=0 then --neutral/falling
                if objAPI:gravityCheck(false,self.pos.x,self.pos.y,0) then --not standing on floor
                    if objAPI:gravityCheck(false,self.pos.x,self.pos.y,-math.floor(self.velocity.y)) then
                        self.pos.y=self.pos.y-math.floor(self.velocity.y)
                    else
                        local plot=pixel2plot(self.pos.x,self.pos.y-math.floor(self.velocity.y),true)
                        self.pos.y=plot2pixel(plot[1],plot[2],true)[2]  --snap to floor
                    end
                    if self.velocity.y==0 then
                        self.velocity.y=0.5
                    end
                else --on floor
                    self.velocity.y=0
                end
            else --ascending
                if objAPI:bumpCheck(false,self.pos.x,self.pos.y,self.velocity.y) then
                    self.pos.y=self.pos.y-self.velocity.y
                else
                    self.velocity.y=-0.5 --fall down
                end
            end
    --ANIMATION
            if not self.dead then
                self.status=((math.ceil((framesPassed/4)))%2)+1
            end
        elseif self.status==4 then
            self.pos.x=self.pos.x+(2*self.dir)
            if self.velocity.y<-0.5 then
                self.pos.y=self.pos.y+self.velocity.y
                self.velocity.y=(self.velocity.y+0.2)*0.8
            elseif (self.velocity.y<0 and self.velocity.y>-0.5) or self.velocity.y>0 then
                self.velocity.y=(math.abs(self.velocity.y)+0.3)*1.09
                self.pos.y=self.pos.y+self.velocity.y
            end
            if self.pos.y<0 then
                objAPI:destroy(self.classID,self.LEVEL)
            end
        elseif self.status==3 and (self.deathAnimTimer<playStage.framesPassed) then
            objAPI:destroy(self.classID,self.LEVEL)
        end
    end
    
    function objGoomba:hit(circumstance)
        if not self.dead then
            self.velocity.y=-11
            self.dead=true
            self.status=4
            if circumstance=="fireball" or circumstance=="block" then
                objAPI:addStats("points","100",self.pos.x,self.pos.y)
            end
            if circumstance=="mario" or circumstance=="fireball" then
                if mario.pos.x<self.pos.x then
                    self.dir=1 -- -1 is left, 1 is right
                else self.dir=-1 end
            end
        end
    end

    function objGoomba:draw(gc)
        gc:drawImage(_G["texture_"..self.TYPE..self.status],self.pos.x-playStage.cameraOffset,self.pos.y+8)
    end

