objShell=class()

    function objShell:init(classID,posX,posY,TYPE,velocity,fromKoopa) --eg ("shell_g77215",64,64,"shell_g",-4)
        self.velocity={}
        if velocity==nil or velocity==0 then 
            self.velocity.x=0 
            if fromKoopa==true then self.koopaTimer=playStage.framesPassed+200 else self.koopaTimer=false end
        else self.velocity.x=velocity self.koopaTimer=false end
        self.velocity.y=0
        self.LEVEL="entityListInner"
        self.TYPE=TYPE
        self.classID=classID
        self.pos={}
        self.fromKoopa=fromKoopa
        self.pos.x=posX
        self.pos.y=posY
        self.status=1
        self.dead=false
        self.hitTimer=0
        self.despawnable=false
        self.hitBox={16,16,true,true}
        self.hitCount=0
    end

    function objShell:logic() --handle both movement and animation
        if self.pos.y>212 then
            self.dead=true
        end
        if not self.dead then
    --CHECK IF MARIO COLLIDED
            if not (mario.starTimer>playStage.framesPassed) then
                local marioSize=16
                if mario.power==0 or mario.crouch then marioSize=0 end            
                if checkCollision(mario.pos.x+1,mario.pos.y-marioSize+1,14,14+marioSize, self.pos.x+1,self.pos.y,14,1) and mario.velocity.y<0 and (self.hitTimer<playStage.framesPassed) then --hit from above
                    self.hitTimer=playStage.framesPassed+6 --avoid instakill after kicking shell or double hits
                    mario:handleStomp()
                    self.hitCount=0 self.hitCountSTOR=0
                    local shakeCondition=true
                    if self.koopaTimer~=false then if (self.koopaTimer-45<playStage.framesPassed) then shakeCondition=false end end
                    if self.velocity.x==0 and shakeCondition then
                        self.koopaTimer=false
                        mario.velocity.tempY=8
                        if self.pos.x>mario.pos.x then
                            self.velocity.x=4
                            self.pos.x=math.ceil(self.pos.x/4)*4 --align to grid
                        else
                            self.velocity.x=-4
                            self.pos.x=math.floor(self.pos.x/4)*4 --align to grid
                        end
                    else self.velocity.x=0 self.koopaTimer=playStage.framesPassed+200 mario.velocity.tempY=15 end
                elseif checkCollision(mario.pos.x+1,mario.pos.y-marioSize+1,14,14+marioSize, self.pos.x+1,self.pos.y+3,14,12) and (self.hitTimer<playStage.framesPassed) then --hit mario from side
                    self.hitTimer=playStage.framesPassed+6
                    if self.velocity.x==0 then
                        self.koopaTimer=false
                        objAPI:addStats("points",400,self.pos.x,self.pos.y)
                        self.hitCount=0 self.hitCountSTOR=0
                        if self.pos.x>mario.pos.x then
                            self.velocity.x=8
                            self.pos.x=math.ceil(self.pos.x/8)*8 --align to grid
                        else
                            self.velocity.x=-8
                            self.pos.x=math.floor(self.pos.x/8)*8 --align to grid
                        end
                    else
                        mario:powerDownMario() end
                end
            end
    --ADD SHELL BOUNDARY
            if self.velocity.x~=0 then
                objAPI:addHitBox(self.classID,self.pos.x,self.pos.y,16,16,"shell")
            end
    --X AXIS
            if objAPI:aggregateCheckX(false,self.pos.x,self.pos.y,self.velocity.x,0,true) then
                self.pos.x=self.pos.x+(self.velocity.x)
            else
                local offset=0
                if self.velocity.x>0 then offset=16 end
                if pixel2bumpable(self.pos.x+self.velocity.x+offset,self.pos.y+15,true) then
                    local v=pixel2plot(self.pos.x+self.velocity.x+offset,self.pos.y+15,true)
                    objAPI:handleBumpedBlock(v[1],v[2],true) end
                self.velocity.x=-self.velocity.x
                if objAPI:aggregateCheckX(false,self.pos.x,self.pos.y,self.velocity.x,0,true) then
                    self.pos.x=self.pos.x+(self.velocity.x)
                elseif not objAPI:aggregateCheckX(false,self.pos.x,self.pos.y,0,0,true) then
                    objAPI:destroy(self.classID,self.LEVEL) end
            end
            if not objAPI:aggregateCheckX(false,self.pos.x,self.pos.y,0,0,true) then --stuck in a block
                objAPI:destroy(self.classID,self.LEVEL) end
            if self.velocity.x~=0 then
                objAPI:coinCheck(self.pos.x,self.pos.y,0)
            end
    --Y AXIS
            if self.velocity.y>0 then --ascending
                if self.velocity.y>0.5 then --significant velocity
                    self.velocity.y=self.velocity.y*0.7
                else --small velocity
                    self.velocity.y=-0.5
                end
            elseif self.velocity.y<0 then --descending
                if self.velocity.y>-6 then --significant velocity
                    self.velocity.y=self.velocity.y*1.5
                end
                if self.velocity.y<-6 then
                    self.velocity.y=-6
                    self.pos.y=math.floor(self.pos.y/6)*6
                end
            end
            if self.velocity.y<=0 then --neutral/falling
                if objAPI:gravityCheck(false,self.pos.x,self.pos.y,0) then --not standing on floor
                    table.insert(debugBoxes,{self.pos.x,self.pos.y,16,16})
                    if self.velocity.y==0 then
                        self.velocity.y=-1
                    end
                    if objAPI:gravityCheck(false,self.pos.x,self.pos.y,-math.floor(self.velocity.y)) then
                        table.insert(debugBoxes,{self.pos.x+2,self.pos.y+2,3,3})
                        self.pos.y=self.pos.y-math.floor(self.velocity.y)
                    else
                        table.insert(debugBoxes,{self.pos.x+6,self.pos.y+6,3,3})
                        local plot=pixel2plot(self.pos.x,self.pos.y-math.floor(self.velocity.y),true)
                        self.pos.y=plot2pixel(plot[1],plot[2],true)[2]  --snap to floor
                    end
                else --on floor
                    self.velocity.y=0
                end
            else --ascending
                if objAPI:bumpCheck(false,self.pos.x,self.pos.y,self.velocity.y) then
                    self.pos.y=self.pos.y-self.velocity.y
                else
                    self.velocity.y=-0.5 --fall down
                end
            end
    --ANIMATION
            if not self.dead then
                if self.koopaTimer==false then
                    self.status=1
                elseif self.fromKoopa then
                    if self.koopaTimer<playStage.framesPassed then
                        objAPI:createObj("koopa"..string.sub(self.TYPE,6,8),self.pos.x,self.pos.y)
                        objAPI:destroy(self.classID,self.LEVEL)
                        self.status=0
                    elseif (self.koopaTimer-7<playStage.framesPassed) then
                        self.status=2
                    elseif (self.koopaTimer-45<playStage.framesPassed) then
                        local animOption=((math.ceil((playStage.framesPassed)))%4)
                        if animOption==0 or animOption==2 then self.status=1
                        elseif animOption==1 then self.status=4
                        else self.status=5 end
                    else
                        self.status=1
                    end
                else
                    self.koopaTimer=false
                end
            end
        elseif self.dead then
            self.pos.x=self.pos.x+(2*self.dir)
            if self.velocity.y<-0.5 then
                self.pos.y=self.pos.y+self.velocity.y
                self.velocity.y=(self.velocity.y+0.2)*0.8
            elseif (self.velocity.y<0 and self.velocity.y>-0.5) or self.velocity.y>0 then
                self.velocity.y=(math.abs(self.velocity.y)+0.3)*1.09
                self.pos.y=self.pos.y+self.velocity.y
            end
        end
    end

