objPowerUp=class()

    function objPowerUp:init(classID,posX,posY,TYPE,fromBlock) --eg ("mushroom37253",64,16,"mushroom",true)
        self.velocity={}
        self.velocity.y=0
        self.LEVEL="entityListInner"
        if string.sub(TYPE,1,1)=="P" then
            if mario.power==0 then
                self.TYPE="mushroom"
                self.status=""
            elseif mario.power>0 then
                self.TYPE="fireflower"
                self.status=1
            end
        else
            self.TYPE=TYPE
            if self.TYPE=="mushroom" or self.TYPE=="mushroom1up" then
                self.status=""
            else
                self.status=1
            end
        end
        self.classID=classID
        self.pos={}
        self.pos.x=posX
        self.pos.y=posY
        self.hitBox={16,16,false,false}
        self.despawnable=fromBlock
        if fromBlock==true then
            self.blockTimer=playStage.framesPassed+4
            self.pos.y=self.pos.y-3.2
        else self.blockTimer=playStage.framesPassed end
        if mario.pos.x>self.pos.x then
            self.dir=1 -- -1 is left, 1 is right
        else self.dir=-1 end
    end

    function objPowerUp:logic() --handle both movement and animation
    --CHECK IF MARIO COLLECTED
        local marioSize=16
        if mario.power==0 or mario.crouch then marioSize=0 end
        if self.blockTimer<playStage.framesPassed then
            if checkCollision(mario.pos.x+1,mario.pos.y-marioSize+1,14,14+marioSize, self.pos.x+1,self.pos.y+1,14,14) then
                objAPI:destroy(self.classID,self.LEVEL)
                if self.TYPE=="mushroom1up" then
                    objAPI:addStats("1up",1,self.pos.x,self.pos.y)
                else
                    if self.TYPE=="mushroom" then
                        mario:powerUpMario(1)
                    elseif self.TYPE=="fireflower" then
                        mario:powerUpMario(2)
                    elseif self.TYPE=="star" then
                        mario:powerStarMario()
                    end
                    objAPI:addStats("points",1000,self.pos.x,self.pos.y)
                end
                return
            end
    --X AXIS
            if self.TYPE~="fireflower" then
                if objAPI:aggregateCheckX(false,self.pos.x,self.pos.y,self.dir*2,0,true) then
                    self.pos.x=self.pos.x+(self.dir*2)
                else
                    self.dir=-self.dir
                    if objAPI:aggregateCheckX(false,self.pos.x,self.pos.y,self.dir*2,0,true,{8,8}) then
                        self.pos.x=self.pos.x+(self.dir*2)
                    elseif not objAPI:aggregateCheckX(false,self.pos.x,self.pos.y,0,0,true) then
                        objAPI:destroy(self.classID,self.LEVEL) return end
                end
            end
    --Y AXIS
            if self.velocity.y>0 then --ascending
                if self.velocity.y>0.5 then --significant velocity
                    self.velocity.y=self.velocity.y*0.7
                else --small velocity
                    self.velocity.y=-0.5
                end
            elseif self.velocity.y<0 then --descending
                if self.velocity.y>-6 then --significant velocity
                    self.velocity.y=self.velocity.y*1.5
                end
                if self.velocity.y<-6 then
                    self.velocity.y=-6
                    self.pos.y=math.floor(self.pos.y/6)*6
                end
            end
            if self.velocity.y<=0 then --neutral/falling
                if objAPI:gravityCheck(false,self.pos.x,self.pos.y,0) then --not standing on floor
                    if objAPI:gravityCheck(false,self.pos.x,self.pos.y,-math.floor(self.velocity.y)) then
                        self.pos.y=self.pos.y-math.floor(self.velocity.y)
                    else
                        local plot=pixel2plot(self.pos.x,self.pos.y-math.floor(self.velocity.y),true)
                        self.pos.y=plot2pixel(plot[1],plot[2],true)[2]  --snap to floor
                    end
                    if self.velocity.y==0 then
                        self.velocity.y=0.5
                    end
                elseif self.TYPE=="star" then --star
                    self.velocity.y=10
                else --on floor
                    self.velocity.y=0
                end
            else --ascending
                if objAPI:bumpCheck(false,self.pos.x,self.pos.y,self.velocity.y) then
                    self.pos.y=self.pos.y-self.velocity.y
                else
                    self.velocity.y=-0.5 --fall down
                end
            end
    --ANIMATION
        else
            self.pos.y=self.pos.y-3.2 --rise from block
        end
        if self.TYPE=="fireflower" or self.TYPE=="star" then
            self.status=((math.ceil((framesPassed/flashingDelay)))%4)+1
        end
    end

    function objPowerUp:hit(circumstance)
        if self.velocity.y<=0 and self.blockTimer<playStage.framesPassed then
            self.velocity.y=10
        end
    end

    function objPowerUp:draw(gc)
        gc:drawImage(_G["texture_"..self.TYPE..self.status],self.pos.x-playStage.cameraOffset,self.pos.y+8) --
    end

