        self.pos.y=yPos
        self.LEVEL="entityListParticle"
        self.dead=false
        self.timer=false
        self.velocity=-0.5
        self.despawnable=true
        self.status=((math.ceil((framesPassed/2)))%4)+1
    end

    function objFireball:gravityCheck(yVel)
        local Y=math.floor(self.pos.y/4)*4
        return (objAPI:moveCheckY(true,self.pos.x,Y+8,yVel,true) and objAPI:moveCheckY(true,self.pos.x+8,Y+8,yVel,true))
    end

    function objFireball:handleFireballHit()
        self.dead=true
        self.timer=1
        self.pos.x=self.pos.x-4
        self.pos.y=self.pos.y-4
        self.TYPE="fireball_A"
        self.status=1
    end

    function objFireball:logic()
        if not self.dead then
            objAPI:addHitBox(self.classID,self.pos.x,self.pos.y,8,8,"fireball")
    --X AXIS
            if objAPI:moveCheckX(true,self.pos.x+4,self.pos.y+2,self.dir*6,true) then
                self.pos.x=self.pos.x+(self.dir*6)
            else
                self:handleFireballHit() return
            end
    --Y AXIS
            if self.velocity>0 then --ascending
                if self.velocity>0.5 then --significant velocity
                    self.velocity=self.velocity*0.5
                else --small velocity
                    self.velocity=-0.5
                end
            elseif self.velocity<0 then --descending
                if self.velocity>-4 then --significant velocity
                    self.velocity=self.velocity*1.7
                end
                if self.velocity<-4 then
                    self.velocity=-4
                end
            end
            if self.velocity>0 then
                if objAPI:bumpCheck(true,self.pos.x,self.pos.y,self.velocity) then
                    self.pos.y=self.pos.y-self.velocity
                else
                    self:handleFireballHit() return
                end
            elseif self:gravityCheck(0) then --not standing on floor
                if self:gravityCheck(math.floor(self.velocity)) then
                    self.pos.y=self.pos.y-math.floor(self.velocity)
                end
            else
                self.velocity=16
            end
    --DEAD
        else
            self.timer=self.timer+1
            if self.timer>=(flashingDelay*3)+1 then
                objAPI:destroy(self.classID,self.LEVEL) return
            end
        end
    --ANIMATION
        if not self.dead then
            self.status=((math.ceil((framesPassed/2)))%4)+1
        else
            self.status=math.ceil(self.timer/flashingDelay)
        end
    end

    function objFireball:draw(gc) --texture_fireball_R1
        gc:drawImage(_G["texture_"..self.TYPE..self.status],self.pos.x-playStage.cameraOffset,self.pos.y+8)
    end

