objBumpedBlock=class()

    function objBumpedBlock:create(blockX,blockY,TYPE,replaceWith)
        local classID="bumpedBlock"..#playStage.entityListOuter+#playStage.entityListInner+1+framesPassed+math.random(1,99999) --assign random ID
        table.insert(playStage.entityListOuter,tostring(classID))
        _G[classID]=objBumpedBlock(classID,blockX,blockY,TYPE,replaceWith)
    end
        
        
    function objBumpedBlock:init(classID,blockX,blockY,TYPE,replaceWith) --eg (23,6,"UsedBlock",false)
        self.LEVEL="entityListOuter"
        self.classID=classID
        self.pos={}
        local v=plot2pixel(blockX,blockY)
        self.pos.x=v[1]
        self.pos.y=v[2]
        self.pos.yA=self.pos.y
        self.TYPE=TYPE
        self.replaceWith={blockX,blockY,replaceWith}
        self.animCount=0
        self.despawnable=true
        plot2place(99,blockX,blockY) --barrier
    end

    function objBumpedBlock:logic()
        if self.animCount<4 then
            objAPI:addHitBox(nil,self.pos.x,self.pos.y-16,16,16,"block")
            table.insert(debugBoxes,{self.pos.x,self.pos.y-16,16,16})
        end
        if self.animCount<=4 then
            self.animCount=self.animCount+1
            self.pos.yA=self.pos.y-round(((math.sin((self.animCount*30)/57.296))*8),0)
        else
            objAPI:destroy(self.classID,self.LEVEL)
            plot2place(self.replaceWith[3],self.replaceWith[1],self.replaceWith[2])
        end
    end

    function objBumpedBlock:draw(gc)
        gc:drawImage(_G["texture_"..self.TYPE], self.pos.x-playStage.cameraOffset, self.pos.yA+8)
    end

