    function objMultiCoinBlock:init(classID,xPos,yPos)
        self.LEVEL="entityListBackground" --doesnt matter anyway
        self.classID=classID
        self.pos={}
        self.pos.x=xPos
        self.pos.y=yPos
        self.TYPE="multicoin"
        self.despawnable=false
        self.GLOBAL=true
        self.timer=0
        objAPI:createObj("coin",self.pos.x,self.pos.y,true)
    end

    function objMultiCoinBlock:logic()
        self.timer=self.timer+1
        if self.timer>100 then --just to be safe
            objAPI:destroy(self.classID,self.LEVEL)
        elseif self.timer==100 then
            if (pixel2ID(self.pos.x+16,self.pos.y)~=99) then
                pixel2place(30,self.pos.x+16,self.pos.y) --get rid of the infinite coin block at all costs
            end
            for i=1,#playStage.entityListOuter do --now THIS is a stupid workaround to a problem i caused, finds the bumped block animation and changes what it replaces
                local classID=_G[playStage.entityListOuter[i]].classID
                if string.sub(classID,1,11)=="bumpedBlock" and _G[classID].pos.x==self.pos.x and _G[classID].pos.y==self.pos.y then
                    _G[classID].replaceWith[3]=30
                end
            end
        end
        despook=self.timer
    end

    function objMultiCoinBlock:draw(gc)
    end

