objBrickParticle=class()

    function objBrickParticle:init(classID,xPos,yPos,thrustX,thrustY)
        self.classID=classID
        self.pos={}
        self.velocity={}
        self.velocity.x=thrustX*0.4
        self.velocity.y=math.abs(thrustY*8)
        self.pos.x=xPos
        self.pos.y=yPos
        self.animIndex=#playStage.entityListParticle%4
        self.LEVEL="entityListParticle"
        self.status=1
        self.delay=true
        self.xAnimTimer=playStage.framesPassed+15
        self.GLOBAL=true
    end

    function objBrickParticle:logic() 
    --ANIMATION
        self.status=((math.ceil((playStage.framesPassed/3)+self.animIndex))%4)+1
    --INITIAL FRAME
        if self.delay==true then self.delay=false return end
    --X AXIS
        if self.xAnimTimer>playStage.framesPassed then
            self.pos.x=self.pos.x+self.velocity.x end
    --Y AXIS
        if self.pos.y>216 then
            objAPI:destroy(self.classID,self.LEVEL) return
        elseif self.velocity.y>0 and self.velocity.y<0.7 then
            self.velocity.y=-0.5
        elseif self.velocity.y>0 then
            self.velocity.y=self.velocity.y*0.4
        elseif self.velocity.y<0 then
            self.velocity.y=(self.velocity.y-0.6)
        end
        self.pos.y=self.pos.y-(self.velocity.y*0.8)
    end

    function objBrickParticle:draw(gc)
        gc:drawImage(_G["texture_brick_piece"..self.status],self.pos.x-playStage.cameraOffset,self.pos.y+8)
    end
