    function playStage:init(levelData)
        playStage.framesPassed=0
        playStage.framesPassedBlock=0
        playStage.coinCount=0
        playStage.SCORE=0
        playStage.currentLevel={}
        playStage.permLevel={}
        playStage:generate()
        playStage.cameraOffset=0
        mario.active=true
        playStage.active=true
        playStage.wait=false
        mario:init()
    end

    function playStage:handleInput(INPUT)
        if INPUT~=nil then
            input[INPUT]=1
            if INPUT=="down" then
                input.stor.down=8
            elseif INPUT=="up" then
                input.stor.up=2
            elseif INPUT=="action" then
                input.stor.action=2
            else
                input.stor[INPUT]=6
            end
        else
            input.stor.up=input.stor.up-1
            input.stor.down=input.stor.down-1
            input.stor.left=input.stor.left-1
            input.stor.right=input.stor.right-1
            input.stor.action=input.stor.action-1

            if input.stor.up>0 then input.up=1 else input.up=0 end
            if input.stor.down>0 then input.down=1 else input.down=0 end
            if input.stor.left>0 then input.left=1 else input.left=0 end
            if input.stor.right>0 then input.right=1 else input.right=0 end
            if input.stor.action>0 then input.action=1 else input.action=0 end
        end
    end

    function playStage:reset()
        playStage:randomise()
        mario:resetPos()
    end

    function playStage:randomise()
        for i=1,13 do --y axis
            for i2=0,50 do --x axis
                playStage.permLevel["x"..i2.."y"..i]=math.random(-2,1)
                if math.random(1,15)==1 then playStage.permLevel["x"..i2.."y"..i]=2 end
            end
        end
        playStage.permLevel["x2y12"]=0
        playStage.currentLevel=copyLevel(playStage.permLevel)
    end

    function playStage:drawTerrain(gc) --rendered in rows from bottom to top w/ the rows from left to right
        for i=1,13 do --bottom to top, vertically (row 14 is reserved for info and is not drawn)
            for i2=math.ceil(playStage.cameraOffset/16),math.ceil((320+playStage.cameraOffset)/16) do --left to right, horizontally, only draw what is visible on screen
                local blockID=getID(i2,i)
                if type(blockID)=='number' then --its a tile
                    if blockID<0 then blockID=0 end
                    local frameForAnim=(math.floor((playStage.framesPassedBlock/4)%#blockIndex[blockID]["texture"]))+1 --(support for animations)
                    if blockIndex[blockID]["texture"][frameForAnim]~=nil then
                        gc:drawImage(_G[tostring(blockIndex[blockID]["texture"][frameForAnim])], ((i2-1)*16)-playStage.cameraOffset, 212-16*(i)+8) end
                else --load the object
                    plot2place(0,i2,i) --blank it out
                    objAPI:createObj(blockID,(i2-1)*16,212-16*(i),false)
    end end end end

    function playStage:levelLogic()
        if not playStage.wait then
            playStage.framesPassed=playStage.framesPassed+1
        end
        playStage.framesPassedBlock=playStage.framesPassedBlock+1
        playStage.TIME=500-(math.floor(playStage.framesPassed/18))
    end

    function playStage:objLogic()
        if not playStage.wait and not mario.powerUp then
            local spawnOffsets={(math.ceil(playStage.cameraOffset/16)*16)-48,((math.ceil(320+playStage.cameraOffset)/16)*16)+48} --view distance plus 3 blocks
            for list=1,#playStage.entityLists do --do all entity lists
                local focusedList=tostring(playStage.entityLists[list])
                for i=1,#playStage[focusedList] do --for all entities within the list
                    if _G[tostring(playStage[focusedList][i])]~=nil then --if entity exists
                        if (((_G[tostring(playStage[focusedList][i])].pos.x) > (spawnOffsets[1])) and ((_G[tostring(playStage[focusedList][i])].pos.x) < (spawnOffsets[2])) and not (( _G[tostring(playStage[focusedList][i])].pos.y)>250)) or ( _G[tostring(playStage[focusedList][i])].GLOBAL==true) then --if in view distance
                            _G[tostring(playStage[focusedList][i])]:logic()
                        elseif (( _G[tostring(playStage[focusedList][i])].pos.y)>212) then
                            objAPI:destroy(_G[tostring(playStage[focusedList][i])].classID,_G[tostring(playStage[focusedList][i])].LEVEL)
                        elseif _G[tostring(playStage[focusedList][i])].despawnable then
                            if _G[tostring(playStage[focusedList][i])].pos.x<-16 or (_G[tostring(playStage[focusedList][i])].pos.x < spawnOffsets[1]+8) or ((_G[tostring(playStage[focusedList][i])].pos.x) > spawnOffsets[2]-8) then
                                objAPI:destroy(_G[tostring(playStage[focusedList][i])].classID,_G[tostring(playStage[focusedList][i])].LEVEL) end
                        end
                    else table.remove(playStage[focusedList],i) end --get rid of blank entities that may occur as a result of overloading, NOT a substantial issue
    end end end end

    function playStage:objDraw(gc,entityLists)
        local spawnOffsets={(math.ceil(playStage.cameraOffset/16)*16)-48,((math.ceil(320+playStage.cameraOffset)/16)*16)+48} --view distance plus 3 blocks
        for list=1,#entityLists do --do all entity lists
            local focusedList=tostring(entityLists[list])
            for i=1,#playStage[focusedList] do
                if _G[tostring(playStage[focusedList][i])]~=nil then
                    if (not ((_G[tostring(playStage[focusedList][i])].pos.x) < (spawnOffsets[1])) and not ((_G[tostring(playStage[focusedList][i])].pos.x) > (spawnOffsets[2]))) or _G[tostring(playStage[focusedList][i])].GLOBAL==true then
                        _G[playStage[focusedList][i]]:draw(gc) end end
            end
        end
    end

    function playStage:paint(gc)
        if mario.pos.x>96 then playStage.cameraOffset=mario.pos.x-96 end --if progressed past initial 96 pixels/6 blocks
        if mario.pos.x<96 then playStage.cameraOffset=0 end
        if mario.pos.x>((playStage.currentLevel.END-14)*16) then playStage.cameraOffset=((playStage.currentLevel.END-14)*16)-96 end --end of stage as declared by the level data
        gc:setColorRGB(97, 133, 248)
        gc:fillRect(0,0,320,218) --backdrop
        gc:setColorRGB(255,255,100)   
    
    --logic
        if not playStage.pause then
            playStage:handleInput() --receive information from keys pressed and parse it
            playStage:levelLogic() --timer etc
            playStage:objLogic() --logic for every obj (powerups, enemies etc) except mario
            objAPI:cleanup() --transfers layers, destroys queued objects
            mario:logic()
        end
        
    --drawing (terrain and most objs)
        playStage:objDraw(gc,{"entityListBackground"})
        playStage:drawTerrain(gc)
        playStage:objDraw(gc,{"entityListInner","entityListOuter"})

        mario:draw(gc)

    --particles
        playStage:objDraw(gc,{"entityListParticle"})
    
    --hud (coins= %^&)
        local frameForAnim=(math.floor((framesPassed/4)%6))+1
        if frameForAnim<4 then frameForAnim="%" elseif frameForAnim==5 then frameForAnim="&" else frameForAnim="^" end
        local hud1=frameForAnim.."+"..addZeros(playStage.coinCount,2)
        local hud2=addZeros(playStage.SCORE,6).." > "..addZeros(playStage.TIME,3)
        if false then hud1="< +".."lives".." " end
        drawFont(gc,hud1,2,2,"left")
        drawFont(gc,hud2,316,2,"right")

end
