function on.paint(gc)
    framesPassed=framesPassed+1 --global framecount
    playStage:paint(gc)
    local highlightedx=pixel2plot(mousePos.x,mousePos.y-8)[1]
    local highlightedy=pixel2plot(mousePos.x,mousePos.y-8)[2]
    if debug then
        local ID=blockSelectionListTEMP[(blockSelectionTEMP%(#blockSelectionListTEMP))+1]
        local name=""
        if blockIndex[ID]~=nil then
            name=(" ("..blockIndex[ID]["name"]..") ")
        end
        gc:drawString("select: "..ID..name.." velX: "..mario.velocity.x.." velY: "..mario.velocity.y, 0, 17, top)
        
        gc:drawString("("..(highlightedx-1)..": "..(13-highlightedy)..") despook: "..despook.." entities: "..#playStage.entityListOuter+#playStage.entityListParticle+#playStage.entityListInner+#playStage.entityListBackground, 0, 32, top)
        gc:drawString("blockX "..highlightedx.." blockY "..highlightedy.." id: "..getID(highlightedx,highlightedy).." x"..mousePos.x.."y"..mousePos.y-8, 0, 48, top)
        --gc:drawString("("..(highlightedx-1)..": "..(13-highlightedy)..") id: "..getID(highlightedx,highlightedy), 0, 48, top) --this is for translating GreatEd maps

        for i=1,#debugBoxes do
            if debugBoxes[i][5]==true then --NOT global
                gc:drawRect(debugBoxes[i][1],debugBoxes[i][2]+8,debugBoxes[i][3],debugBoxes[i][4])
            else
                gc:drawRect(debugBoxes[i][1]-playStage.cameraOffset,debugBoxes[i][2]+8,debugBoxes[i][3],debugBoxes[i][4])
            end
        end
        debugBoxes={}
    end
    gc:setColorRGB(97, 133, 248)
    gc:fillRect(0,200,60,212)
    gc:setColorRGB(255,255,100) 
    
    drawFont(gc,tostring("v"..versText), 2, 203,nil,0)
    local pixels = plot2pixel(highlightedx,highlightedy,true)
    gc:drawRect(pixels[1],pixels[2]+8,16,16)
end


