objAPI=class()

    function objAPI:moveCheckX(isMario,xPos,yPos,xVel,ignoreEdge) --CHECKS FOR INTERFERENCE IN X AXIS returns true if nothing in the way
        --X AXIS HANDLING
        if ((xPos+xVel<0) or (xPos+xVel>(currentLevel.END*16))) and not ignoreEdge then --edge of screen LEFT
            return false
        else --no edges of screen
            xPos=xPos+xVel --temp set to new pos
        end
        if (pixel2solid(xPos+(xVel/16)-playStage.cameraOffset,yPos) and not (isMario and pixel2anything("marioonly",xPos+(xVel/16)-playStage.cameraOffset,yPos))) or (isMario and pixel2anything("entityonly",xPos+(xVel/16)-playStage.cameraOffset,yPos)) then --check if new x pos in a wall
            return false
        else return true
        end
    end

    function objAPI:moveCheckY(isMario,xPos,yPos,yVel,notBump) --CHECKS FOR INTERFERENCE IN Y AXIS returns true if nothing in the way returns false if wall
        yPos=math.floor(yPos)
        --Y AXIS HANDLING... obviously
        local result=true
        if notBump then --platform collision
            for i=1,#playStage.platformList do 
                local pX=playStage.platformList[i][2] local pY=playStage.platformList[i][3] local pW=playStage.platformList[i][4]
                if xPos>=pX and xPos<=pX+pW then --object is in x axis radius of platform, therefore possibility of landing
                    if yPos<pY and yPos+yVel<pY then --above platform and wont land on it
                    elseif yPos+yVel==pY then --on platform
                        result=false --TEMP
                    elseif yPos<pY and not (yPos+yVel<pY) then --above platform but will go through it
                        result=false
                    end
                else
                end
            end
        end
        if yVel==0 and notBump and pixel2semisolid(xPos-playStage.cameraOffset,yPos) and result==true then --this could be optimised but i currently have no clue how, will come back to this when im less stupid
            if (math.floor(yPos-4)%16)==0 then --confirm standing at the y level of the top of a block, i did so much bullshit before finally realising i could do this
                return false
            else return true end --  these are all split for clarity
        elseif (pixel2solid(xPos-playStage.cameraOffset,yPos+(yVel)) and not (isMario and pixel2anything("marioonly",xPos-playStage.cameraOffset,yPos+(yVel)))) then --check if new y pos in a wall
            return false --  if block is solid
        elseif isMario and pixel2anything("entityonly",xPos-playStage.cameraOffset,yPos+(yVel)) then
            return false --  if 1. it is mario 2. if it's entityonly then it is solid for him. necessary as entityonly blocks do not have the general 'solid' parameter
        elseif isMario and yVel<0 and pixel2anything("invisiblock",xPos-playStage.cameraOffset,yPos+(yVel)) then
            return false --  if mario is jumping upwards, then if he encounters an invisible block bump it
        elseif pixel2semisolid(xPos-playStage.cameraOffset,yPos+(yVel)) and (yVel>=0.1) and notBump then
            if (math.floor(yPos+yVel-4)%16)==0 then -- y pos at top of block
                return false
            end
            local v1=pixel2plot(xPos-playStage.cameraOffset,yPos+(yVel))[2] --new proposed pos
            local v2=pixel2plot(xPos-playStage.cameraOffset,yPos+1)[2] --current pos
            if (v1~=v2) then --above top of block and not under + falling through
                return false
            else return true end
        else --the coast is clear...
            return result
        end
    end

    function objAPI:gravityCheck(isMario,xPos,yPos,yVel,turnAround,dir,optionalLength) --CHECKS IF STANDING ON BLOCK returns true if gravity applies
        if turnAround=="koopa_R" then turnAround=true end
        local distance=15
        if optionalLength~=nil then distance=optionalLength end
        local bottomLeft = objAPI:moveCheckY(isMario,xPos+2,yPos+16,yVel,true)
        local bottomRight = objAPI:moveCheckY(isMario,xPos+distance-2,yPos+16,yVel,true)
        if turnAround~=true then
            if (bottomLeft and bottomRight) then
                return true
            else 
                return false
            end
        else
            if ((not bottomLeft and bottomRight) and dir==1) or ((bottomLeft and not bottomRight) and dir==-1) then
                return ("turn")
            elseif (bottomLeft and bottomRight) then
                return true
            else
                return false end
        end
    end

    function objAPI:customCheckX(isMario,xPos,yPos,xVel,w,h,ignoreEdge) --very unused, but may be useful at some point. otherwise i will get rid of it
        local topLeft=objAPI:moveCheckX(isMario,xPos,yPos,xVel,ignoreEdge)
        local topRight=objAPI:moveCheckX(isMario,xPos+w,yPos,xVel,ignoreEdge)
        local bottomLeft=objAPI:moveCheckX(isMario,xPos,yPos+h,xVel,ignoreEdge)
        local bottomRight=objAPI:moveCheckX(isMario,xPos+w,yPos+h,xVel,ignoreEdge)
        return (topLeft and topRight and bottomLeft and bottomRight)
    end

    function objAPI:aggregateCheckX(isMario,xPos,yPos,xVel,powerStatus,ignoreEdge) --checks points at head, feet, left, right
        if powerStatus==nil then powerStatus=0 end
        local moveOffset=0
        if isMario then moveOffset=-1 end
        local topLeft=objAPI:moveCheckX(isMario,xPos+2,yPos+1,xVel,ignoreEdge) --more leniency
        local topRight=objAPI:moveCheckX(isMario,xPos+14+moveOffset,yPos+1,xVel,ignoreEdge) --more leniency, 14 pixel search
        local bottomLeft=objAPI:moveCheckX(isMario,xPos+2,yPos+15,xVel,ignoreEdge)
        local bottomRight=objAPI:moveCheckX(isMario,xPos+14+moveOffset,yPos+15,xVel,ignoreEdge)
        local powerLeft
        local powerRight    --------------------------------------------------------------------------------------------------------------------o_ <--powerLeft/Right, big mario calculated after
        if powerStatus~=0 and not mario.crouch then powerLeft=objAPI:moveCheckX(isMario,xPos+2,yPos-13,xVel) else powerLeft=true end -----------O                   (offset also needed when rendering)
        if powerStatus~=0 and not mario.crouch then powerRight=objAPI:moveCheckX(isMario,xPos+13,yPos-13,xVel) else powerRight=true end--------@=@ <-- topLeft/Right, position always set as small mario height
        if topLeft and topRight and bottomLeft and bottomRight and powerLeft and powerRight then return true    -------------------------------/\  <--bottomLeft/Right
        else return false
        end
    end

    function objAPI:bumpCheck(isMario,xPos,yPos,yVel,powerStatus) --CHECKS IF ABOUT TO COLLIDE WITH BLOCK returns true if bump applies
        yVel=-yVel
        if powerStatus==nil then powerStatus=0 end
        local topLeft = objAPI:moveCheckY(isMario,xPos+2,yPos,yVel,false) --easier to make tight jumps (has to be same as objAPI:gravityCheck or can climb walls) 
        local topRight = objAPI:moveCheckY(isMario,xPos+13,yPos,yVel,false)
        local powerLeft
        local powerRight
        if powerStatus>0 and not mario.crouch then powerLeft=objAPI:moveCheckY(isMario,xPos+2,yPos-15,yVel,false) powerRight=objAPI:moveCheckY(isMario,xPos+13,yPos-15,yVel,false)
        else powerLeft=true powerRight=true end
        if topLeft and topRight and powerLeft and powerRight then
            return true
        else return false
        end
    end

    function objAPI:type2class(TYPE)
        if typeIndex[string.sub(TYPE,1,5)]~=nil then
            return typeIndex[string.sub(TYPE,1,5)][1],typeIndex[string.sub(TYPE,1,5)][2]
        else
            return false,false
        end
    end
    
    function objAPI:type2name(TYPE,statusBox) --statusBox: 0=false, 1=true
        local name=""
        if type(TYPE)=='number' then
            if blockIndex[TYPE]~=nil then
                name=blockIndex[TYPE]["name"]
            end
        elseif string.sub(TYPE,1,8)~="platform" then
            if nameIndex[TYPE]~=nil then name=nameIndex[TYPE] end
        else
            name={} --eg: platform_3~1~lx~64
            local config=splitByChar(string.sub(TYPE,10,#TYPE),"~")
            if nameIndex[config[3]]~=nil then name[1]="Platform "..nameIndex[config[3]] end
            if statusBox==1 then 
                name[2]="Length: "..config[1]
                if string.sub(config[3],1,1)=="l" then name[3]="Distance: "..math.floor(config[4]/16) end
            end
        end return name
    end

    function objAPI:createObj(TYPE,posX,posY,despawnable,arg1,arg2)
        local classID=TYPE..#entityListInner+#entityListParticle+#entityListOuter+#entityListBackground+1+framesPassed.."r"..math.random(0,200) --assign random ID
        local classTYPE local LEVEL
        classTYPE,LEVEL=objAPI:type2class(TYPE)
        if classTYPE~=false then
            _G[tostring(classID)]=_G[classTYPE](classID,posX,posY,TYPE,despawnable,arg1,arg2)  --despawnable also triggers block animation (sometimes)
            table.insert(_G[LEVEL],tostring(classID)) 
        end
    end

    function objAPI:destroy(objectName,LEVEL) --add to cleanup waitlist
        table.insert(cleanupListDestroy,{objectName,LEVEL})
    end

    function objAPI:transferLayer(objectName,LEVEL,newLEVEL) --add to cleanup waitlist
        table.insert(cleanupListTransfer,{objectName,LEVEL,newLEVEL})
    end

    function objAPI:sendToFront(objectName,LEVEL) --removes from layer and reinserts at the top
        table.insert(cleanupListTransfer,{objectName,LEVEL,LEVEL})
    end

    function objAPI:addHitBox(classID,x,y,w,h,TYPE)
        table.insert(hitBoxList,{classID,x,y,w,h,TYPE})
    end

    function objAPI:addPlatform(classID,x,y,w,xVel,yVel)
        local yOffset=0
        if math.abs(yVel)>1 then
            yOffset=(math.floor(y-4)%2)
        end
        table.insert(playStage.platformListAdd,{classID,x,y-yOffset,w,xVel,yVel})
    end

    function objAPI:updatePlatforms()
        playStage.platformList={}
        for i=1,#playStage.platformListAdd do
            table.insert(playStage.platformList,{playStage.platformListAdd[i][1],playStage.platformListAdd[i][2],playStage.platformListAdd[i][3],playStage.platformListAdd[i][4],playStage.platformListAdd[i][5],playStage.platformListAdd[i][6]})
        end
        playStage.platformListAdd={}
    end

    function objAPI:cleanup() --these huge functions relating to every object are very fun :>
        for iH=1,#hitBoxList do --hitBox aggressor array: {classID,x,y,w,h,type} // hitBox passive array: {w,h,willBeKilled,destroyFireball,xOffset,yOffset}
            for list=1,#entityLists do --do all entity lists
                local focusedList=tostring(entityLists[list])
                for i=1,#_G[focusedList] do --for all entities within the list
                    if _G[tostring(_G[focusedList][i])].hitBox then --if entity can be hit
                        local hitBox=_G[tostring(_G[focusedList][i])].hitBox
                        local hitArea=hitBoxList[iH]
                        if hitBox[5]==nil then hitBox[5]=0 end if hitBox[6]==nil then hitBox[6]=0 end
                        local pos={_G[tostring(_G[focusedList][i])].x,_G[tostring(_G[focusedList][i])].y} -- V if there is a collision V
                        local CLASS=_G[tostring(_G[focusedList][i])].classID
                        if tostring(hitBoxList[iH][1])~=CLASS and (checkCollision(hitArea[2],hitArea[3],hitArea[4],hitArea[5],pos[1]+2+hitBox[5],pos[2]+2+hitBox[6],hitBox[1]-4,hitBox[2]-4)) then
                            if _G[tostring(_G[focusedList][i])].dead~=true then
                                if hitBoxList[iH][6]=="shell" and hitBox[3]==true then
                                    _G[tostring(hitArea[1])]:handleShellPoints()
                                elseif hitBoxList[iH][6]=="fireball" and hitBox[4]==true then
                                    _G[tostring(hitArea[1])]:handleFireballHit()
                                elseif hitBoxList[iH][6]=="mario" and not (string.sub(CLASS,1,4)=="mush" or string.sub(CLASS,1,4)=="fire" or string.sub(CLASS,1,4)=="star") then
                                    objAPI:addStats("points","200",mario.x,mario.y-16)
                                end
                            end
                            _G[tostring(_G[focusedList][i])]:hit(hitBoxList[iH][6]) --react to hit (death/jump)
        end end end end end --important for hitbox to go first so that new queued requests don't get cleared
        for i=1,#cleanupListDestroy do --remove entity from list and clear all stored vars
            local LEVEL=cleanupListDestroy[i][2]
            local objectName=cleanupListDestroy[i][1]
            for i2=1,#_G[LEVEL] do
                if tostring(_G[LEVEL][i2])==objectName then
                    table.remove(_G[LEVEL],i2)
                    _G[objectName]=nil
                    break
        end end end
        for i=1,#cleanupListTransfer do
            local LEVEL=cleanupListTransfer[i][2]
            local newLEVEL=cleanupListTransfer[i][3]
            local objectName=cleanupListTransfer[i][1]
            for i2=1,#_G[LEVEL] do
                if tostring(_G[LEVEL][i2])==objectName then
                    table.remove(_G[LEVEL],i2)
                    table.insert(_G[newLEVEL],tostring(objectName))
                    break
        end end end
        cleanupListDestroy={}
        cleanupListTransfer={}
        hitBoxList={}
    end

    function objAPI:handleBumpedBlock(xLOC,yLOC,shell)
        local ID=getID(xLOC,yLOC)
        local pixelXY=plot2pixel(xLOC,yLOC,false)
        if blockIndex[ID].containing~=nil and blockIndex[ID].containing~=false then --if there is something in the block
            objAPI:createObj(blockIndex[ID].containing,pixelXY[1],pixelXY[2],true) --(TYPE,posX,posY,fromBlock) objAPI:createObj(blockID,(i2-1)*16,212-16*(i),0)
        end
        if blockIndex[ID].breakable==false or (mario.power==0 and not shell) then --create bumped block if unable to be destroyed
            objBumpedBlock:create(xLOC,yLOC,blockIndex[ID].bumpable[2],blockIndex[ID].bumpable[3],false)
        else --smash the block
            playStage.SCORE=playStage.SCORE+50
            plot2place(0,xLOC,yLOC)
            objBrickParticleGlobalAnimIndex=0
            objAPI:createObj("brick_piece",pixelXY[1],pixelXY[2],false,-3,5) --top left
            objAPI:createObj("brick_piece",pixelXY[1]+8,pixelXY[2],false,3,5) --top right
            objAPI:createObj("brick_piece",pixelXY[1]+8,pixelXY[2]+8,false,3,2.5) --bottom right
            objAPI:createObj("brick_piece",pixelXY[1],pixelXY[2]+8,false,-3,2.5) --bottom left
            objAPI:addHitBox(nil,pixelXY[1],pixelXY[2]-16,16,16,"block")
        end
        if yLOC<=12 and blockIndex[getID(xLOC,yLOC+1)].coin==true then --if there is a coin above the bumped block
            plot2place(0,xLOC,yLOC+1)
            objAPI:createObj("coin",pixelXY[1],pixelXY[2]-16,true)
        end
    end

    function objAPI:coinCheck(x,y,powerStatus)
        if powerStatus==nil then powerStatus=0 end
        local function collectCoin(x,y)
            objAPI:addStats("coins",1)
            pixel2place(0,x,y,true)
            objAPI:addStats("points",200)
        end
        if pixel2anything("coin",x+2,y,true) then collectCoin(x+2,y) end --topLeft
        if pixel2anything("coin",x+13,y,true) then collectCoin(x+13,y) end --topRight
        if pixel2anything("coin",x+2,y+15,true) then collectCoin(x+2,y+15) end --bottomLeft
        if pixel2anything("coin",x+13,y+15,true) then collectCoin(x+13,y+15) end --bottomRight
        if powerStatus~=0 then
            if pixel2anything("coin",x+2,y-15,true) then collectCoin(x+2,y-15) end --powerLeft
            if pixel2anything("coin",x+13,y-15,true) then collectCoin(x+13,y-15) end --powerRight
        end
    end

    function objAPI:platformCheck(x,y,optionalLength) --checks of standing on a platform and then returns xVel/yVel if applicable
        y=math.floor(y)
        local distance=15-2
        if optionalLength then distance=optionalLength-2 end
        for i=1,#playStage.platformList do 
            local pX=playStage.platformList[i][2] local pY=playStage.platformList[i][3] local pW=playStage.platformList[i][4]
            if ((x+2>=pX and x+2<=pX+pW) or (x+distance>=pX and x+distance<=pX+pW)) and y+16==pY then --object is in x axis radius of platform, and is on same y level
                return {playStage.platformList[i][5],playStage.platformList[i][6]}
            end
        end
        return {0,0}
    end

    function objAPI:addStats(type,value,x,y)
        if type=="points" then
            if x~=nil and y~=nil then objAPI:createObj("score",x,y,nil,value) end --particle
            playStage.SCORE=playStage.SCORE+value
        elseif type=="coins" then
            playStage.coinCount=playStage.coinCount+value
            if playStage.coinCount>99 then
                playStage.coinCount=playStage.coinCount%100
                objAPI:addStats("1up",1,mario.x,mario.y)
            end
        elseif type=="1up" then
            objAPI:createObj("score",x,y,nil,"1up")
        end
    end

