mario=class()

    function mario:init()
        mario:resetPos()
    end

    function mario:resetPos() --default config
        mario.powerUp=false
        mario.powerDown=false
        mario.dead=false
        mario.powerAnimTimer=0
        mario.dir="R"
        mario.crouch=false 
        mario.x=currentLevel.startX mario.y=currentLevel.startY
        mario.vx=0 mario.vy=0
        mario.px=0 mario.py=0
        mario.status="idle"
        mario.power=0
        playStage.wait=false
        mario.iFrames=-1
        mario.hitCount=0
        mario.starAnim=false
        mario.actionAnimTimer=0
        mario.starTimer=0
        mario.jumpAnim=0
    end

    function mario:logic() --direction is seperate as its still needed during pause
        mario.starAnim=false
        if not playStage.wait and not mario.powerUp and not mario.powerDown then
            mario:calculateInput()
            mario:calculateMove()
            objAPI:coinCheck(mario.x,mario.y,mario.power)
        end
        mario:calculateAnim()
    end

    function mario:calculateInput() --turns inputs into velocity (or crouch/fireball)
    --X movement
        if mario.power~=0 and (input.down==1) and mario.vy==0 then
            mario.crouch=true
            mario.actionAnimTimer=0
        elseif mario.power~=0 and ((pixel2solid(mario.x+2-playStage.cameraOffset,mario.y-8) and not pixel2anything("marioonly",mario.x+2-playStage.cameraOffset,mario.y-8)) or (pixel2solid(mario.x+13-playStage.cameraOffset,mario.y-8) and not pixel2anything("marioonly",mario.x+13-playStage.cameraOffset,mario.y-8))) and mario.vy==0 then
            mario.crouch=true
            mario.actionAnimTimer=0
        elseif mario.vy==0 or mario.power==0 then mario.crouch=false end
        if mario.crouch then mario.vx=mario.vx*(0.93) end
        if mario.crouch and mario.vy==0 and (pixel2solid(mario.x+2-playStage.cameraOffset,mario.y-8) or pixel2solid(mario.x+13-playStage.cameraOffset,mario.y-8)) then
            if mario.jumpAnim>-7 and math.abs(mario.vx)<1 then
                if input.right==1 and (mario.vx>0 or mario.jumpAnim>-1) then mario.vx=input.right
                elseif input.left==1 and (mario.vx<0 or mario.jumpAnim>-1) then mario.vx=-input.left end
            end
        elseif (input.left==1 or input.right==1) and (not mario.crouch or mario.vy~=0) then
            if ((input.left==1 and mario.vx>0.5) or (input.right==1 and mario.vx<-0.5)) and mario.vy==0 then mario.vx=mario.vx*0.9 --drifting slower
            else --not drifting
            --max running speed 7
                if math.abs(mario.vx)<2.0 then --walking under 2.0
                    mario.vx=mario.vx+input.right*(math.random(3,5)/10)
                    mario.vx=mario.vx-input.left*(math.random(3,5)/10)
                elseif math.abs(mario.vx)<4.5 then
                    mario.vx=mario.vx+input.right*0.3
                    mario.vx=mario.vx-input.left*0.3
                elseif math.abs(mario.vx)<=7 and mario.vy==0 then
                    mario.vx=mario.vx+input.right*0.12
                    mario.vx=mario.vx-input.left*0.12
                elseif math.abs(mario.vx)<=7 and mario.vy~=0 and ((input.right==1 and mario.vx<-0.5) or (input.left==1 and mario.vx>0.5)) then
                    mario.vx=mario.vx+input.right*0.6
                    mario.vx=mario.vx-input.left*0.6 end end
        elseif not mario.crouch then mario.vx=mario.vx*(0.8)
        end
        if mario.vx>=7 then mario.vx=7 elseif mario.vx<=-7 then mario.vx=-7 end 
        if math.abs(mario.vx)<0.4 then mario.vx=0 end --movement minumum, prevents velocity of 0.00001626 for example
    --Y movement
        if input.up==1 and not objAPI:gravityCheck(true,mario.x,mario.y,-mario.py) then  --up arrow pressed and on the floor (no double jumps)
            local runningBoost=0
            if mario.jumpAnim<=0 then mario.jumpAnim=3 end
            if math.abs(mario.vx)>3 then runningBoost=math.abs(mario.vx) end --momentum calculation
            mario.vy=18+runningBoost --for a maximum of 25, jump ~5.5 blocks. without boost is 4 blocks (idle)
        else mario.vy=mario.vy*0.745 end --slow down upwards velocity when jumping (lower is floatier)
        if math.abs(mario.vy)<0.6 then mario.vy=0 end --movement minumum, prevents velocity of 0.00001626 for example
    --SPECIAL ACTIONS
        if input.action==1 and mario.power==2 and not mario.crouch then
            local fireballCount=0
            for i=1,#entityListParticle do
                if string.sub(entityListParticle[1],1,8)=="fireball" then
                    fireballCount=fireballCount+1
                end
            end
            if fireballCount<2 then
                mario.actionAnimTimer=2
                if mario.dir=="L" then
                    objAPI:createObj("fireball_L",mario.x,mario.y)
                else
                    objAPI:createObj("fireball_R",mario.x+16,mario.y)
                end
            end
        end
    end

    function mario:calculateMove() --use velocity to update position
        if mario.vtempY ~= nil then
            mario.vy=mario.vtempY
            mario.vtempY=nil
        end
        mario.vx=round(mario.vx,2)
        mario.vy=round(mario.vy,2)
    --X handling
        if objAPI:aggregateCheckX(true,mario.x,mario.y,mario.vx+mario.px,mario.power) then --no walls in X direction!
            mario.x=mario.x+mario.vx+mario.px --confirm movement
        else --wall detected
            while not objAPI:aggregateCheckX(true,mario.x,mario.y,mario.vx+mario.px,mario.power) and math.abs(mario.vx)>0.5 do
                mario.vx=mario.vx*0.9 end --slow mario down so he can go closer to wall
            if math.abs(mario.vx)<0.9 then mario.vx=0 mario.px=0 end
            mario.x=mario.x+mario.vx+mario.px
        end
    --Y handling
        if objAPI:aggregateCheckX(true,mario.x,mario.y-mario.py,0,mario.power) then --is where the platform's shoving mario, in a wall?
            mario.y=mario.y-mario.py
        end
        if not objAPI:bumpCheck(true,mario.x,mario.y,mario.vy,mario.power) and mario.vy>0 then --jump too large
            for i=math.floor(mario.vy),0,-1 do
                if objAPI:bumpCheck(true,mario.x,mario.y,i,mario.power) then
                    mario.vy=i+1 break
        end end end
        if not objAPI:bumpCheck(true,mario.x,mario.y,0,mario.power) or not objAPI:bumpCheck(true,mario.x,mario.y,(mario.vy+mario.py)/2,mario.power) then -- check if bumping a block - this shit "fixes" it, idk why but dont touch this
            local bigOffset=0
            if mario.power>0 and not mario.crouch then bigOffset=16 end
            if pixel2bumpable(mario.x+2-playStage.cameraOffset,mario.y-bigOffset-8) or pixel2bumpable(mario.x+13-playStage.cameraOffset,mario.y-bigOffset-8) and mario.vy>0 then --something can be bumped here
                local xOffset
                if pixel2bumpable(mario.x+8-playStage.cameraOffset,mario.y-bigOffset-8) then --prioritise whatever is in the middle
                    xOffset=8
                elseif pixel2bumpable(mario.x+2-playStage.cameraOffset,mario.y-bigOffset-8) then
                    xOffset=2
                else
                    xOffset=13
                end
                local LOCs=pixel2plot(mario.x+xOffset-playStage.cameraOffset,mario.y-bigOffset-8)
                local xLOC=LOCs[1] local yLOC=LOCs[2]
                objAPI:handleBumpedBlock(xLOC,yLOC)
            end
            mario.vy=-0.6
        end
        
        if objAPI:gravityCheck(true,mario.x,mario.y,0) and (mario.vy<0.1) then --gravity occurs!
            local tempVelocity=0
            --print("gravity occurs------------------------",mario.y+16,mario.vy,math.floor(mario.y+16))
            if objAPI:gravityCheck(true,mario.x,mario.y,8) then --large distance between floor
                --print("large distance")
                if mario.vy>-7 then -- terminal velocity: -7
                    mario.vy=mario.vy-1.7
                else mario.vy=-7 end
            elseif objAPI:gravityCheck(true,mario.x,mario.y,mario.vy) then --small distance
                --print("small distance")
                for i=-7,0 do
                    --print(i)
                    if objAPI:gravityCheck(true,mario.x,mario.y,-i) then
                        tempVelocity=i-1
                        mario.vy=0
                        break end end end
            mario.y=mario.y-mario.vy-tempVelocity
            --print("=========END===========",mario.y+16)
        elseif mario.vy>0.1 then
            mario.y=mario.y-mario.vy
        else
            mario.hitCount=0
            mario.vy=0
        end
        mario.px=nil mario.py=nil
        local platformVel=objAPI:platformCheck(self.x,self.y)
        mario.px=platformVel[1]  mario.py=platformVel[2]
    --OTHER (death plane)
        if mario.y>216 then
            mario:kill()
        end
    end

    function mario:calculateAnim(calculateAnimForce) --handles mario's visuals (walk cycles, animations etc)
        if (not mario.powerUp and not mario.powerDown and not playStage.wait and not mario.dead) or calculateAnimForce then --normal gameplay
            mario.powerAnim=mario.power
            if not mario.crouch then
                if mario.vy==0 then
                    if mario.vx~=0 then
                        local velocity2cycle=0
                        if (math.abs(mario.vx))>6.5 then velocity2cycle = 1.2 elseif (math.abs(mario.vx))>4.5 then velocity2cycle = 0.6 elseif (math.abs(mario.vx))>2 then velocity2cycle = 0.3 else velocity2cycle = 0.2 end
                        mario.status="walk"..math.floor((velocity2cycle*playStage.framesPassed)%3)+1 end
                    if input.left==1 and mario.vx>0.01 then mario.status="drift" input.right=0 --drift animation if arrow key is going opposite way to velocity
                    elseif input.right==1 and mario.vx<-0.01 then mario.status="drift" input.left=0 end
                    if mario.vx==0 then mario.status="idle" end
                    else mario.status="jump"
                end
            else
                mario.status="crouch"
            end
            if mario.actionAnimTimer>0 then
                mario.actionAnimTimer=mario.actionAnimTimer-1
                mario.status="fire"
            end
        elseif mario.powerUp and playStage.wait then --powering UP in progress
            if mario.power==1 then --growing to big mario
                if playStage.framesPassedBlock-12<mario.powerAnimTimer then 
                    local animOption=(math.ceil((playStage.framesPassedBlock/2)))%2
                    if animOption==1 then mario.powerAnim=1 mario.status="grow" else mario.powerAnim=0 mario.status="idle" end
                else
                    local animOption=(math.ceil((playStage.framesPassedBlock/2)))%3
                    if animOption==0 then mario.powerAnim=0 mario.status="idle" elseif animOption==1 then mario.powerAnim=1 mario.status="grow" else mario.powerAnim=1 mario.status="idle" end
                end
            elseif mario.power==2 then --growing to fire mario
                local animOption=(math.ceil((playStage.framesPassedBlock/2)))%4
                mario.powerAnim=2
                mario.starAnim=2
            end
            if playStage.framesPassedBlock-24>mario.powerAnimTimer then --end animation
                mario.powerUp=false
                playStage.wait=false
                mario.starAnim=false
            end
        elseif mario.powerDown and playStage.wait then --powering DOWN in progress
            if mario.power==0 then
                local animOption=(math.ceil((playStage.framesPassedBlock/flashingDelay)))%2
                if playStage.framesPassedBlock-12<mario.powerAnimTimer then --powering down to small mario
                    if animOption==1 then mario.powerAnim=mario.power+1 mario.status=mario.animCache else mario.powerAnim=mario.power+1 mario.status="invisible" end --flash
                else --down to big
                    if mario.animCache=="crouch" then
                        mario.animCache="walk1" end --as crouch does not exist for small mario
                    if animOption==1 then mario.powerAnim=mario.power mario.status=mario.animCache else mario.powerAnim=mario.power mario.status="invisible" end --flash
                end
            elseif mario.power==1 then --powering down to big mario
                local animOption=(math.ceil((playStage.framesPassedBlock/3)))%2
                if animOption==1 then
                    mario.powerAnim=1
                else
                    mario.powerAnim=2
                end
            end
            if playStage.framesPassedBlock-24>mario.powerAnimTimer then --end power down anim
                mario.powerDown=false
                playStage.wait=false
                mario.iFrames=playStage.framesPassed+30
            end
        elseif mario.dead then --mario death animation
            mario.powerAnim=0
            if mario.vdeath<-0.5 and (playStage.framesPassedBlock>mario.deathAnimTimer+12) then
                mario.y=mario.y+mario.vdeath
                mario.vdeath=(mario.vdeath+0.2)*0.8
            elseif (mario.vdeath<0 and mario.vdeath>-0.5) or mario.vdeath>0 then
                mario.vdeath=(math.abs(mario.vdeath)+0.3)*1.09
                mario.y=mario.y+mario.vdeath
            end
            if mario.y>220 then
                if not mario.respawnTime then
                    mario.respawnTime=playStage.framesPassedBlock+18
                end
                if playStage.framesPassedBlock>mario.respawnTime then
                    if not playStage.EDITOR then
                        on.escapeKey()
                    else
                        playStage:charIn("e")
                    end
                end
            end
        end
        if (mario.starTimer>playStage.framesPassed) then --handle star anim and hitbox
            if not playStage.wait then
                if (mario.starTimer-70>playStage.framesPassed) then
                    mario.starAnim=2
                elseif (mario.starTimer-15>playStage.framesPassed) then
                    mario.starAnim=5 --slow down anim
                end
            end
            local h=0
            if mario.power>0 then
                h=16
            end
            objAPI:addHitBox("mario",mario.x,mario.y-h,16,16+h,"mario")
        end
        if not playStage.wait and not mario.powerUp and not mario.powerDown and (mario.jumpAnim>0 or (not (mario.crouch and (pixel2ID(mario.x+2-playStage.cameraOffset,mario.y-8)==1 or pixel2ID(mario.x+13-playStage.cameraOffset,mario.y-8)==1)) and not objAPI:gravityCheck(true,mario.x,mario.y,0))) and mario.vy==0 then --on ground, not jumping
            if input.right==1 then mario.dir="R"
            elseif input.left==1 then mario.dir="L" end
        end
    end

    function mario:powerUpMario(optionalPower,forced)
        if not mario.dead and not mario.powerUp and not mario.powerDown then
            local proceed=true
            if optionalPower~=nil and (forced==true or optionalPower>mario.power) then
                mario.power=optionalPower
            elseif optionalPower==nil and mario.power<2 then mario.power=mario.power+1
            else proceed=false end --there is nothing to power to
            if proceed then
                playStage.wait=true
                mario.powerUp=true
                mario.powerDown=false
                mario.powerAnimTimer=playStage.framesPassedBlock
                mario:calculateAnim(true)
            end
        end
    end

    function mario:powerDownMario(optionalPower)
        if mario.power>0 and not mario.dead and not mario.powerDown and not mario.powerUp and not (mario.iFrames>playStage.framesPassed) then mario.power=mario.power-1
            playStage.wait=true
            mario.powerDown=true
            mario.powerUp=false
            mario.powerAnimTimer=playStage.framesPassedBlock
            mario.animCache=mario.status
        elseif not mario.dead and not (mario.iFrames>playStage.framesPassed) and not mario.powerDown and mario.power==0 then
            mario.kill()
        end
    end

    function mario:powerStarMario(optionalLength)
        if optionalLength==nil then
            mario.starTimer=playStage.framesPassed+200
        else
            mario.starTimer=playStage.framesPassed+optionalLength
        end
    end

    function mario:kill()
        mario.vdeath=-11
        mario.respawnTime=false
        mario.status="death"
        playStage.wait=true
        mario.powerDown=false
        mario.powerUp=false
        mario.dead=true
        mario.power=0
        mario.jumpAnim=0
        mario.deathAnimTimer=playStage.framesPassedBlock
    end

    function mario:draw(gc)
        local drawOffset=0
        local star=""
        if mario.powerAnim~=0 then drawOffset=16 end
        if mario.jumpAnim>-7 then 
            if mario.jumpAnim>1 then --literally just so that he makes contact with the floor if jump is held. i know, animating a fake jump that isnt even happening
                if not mario.crouch then mario.status="jump" end
                drawOffset=drawOffset+2
            end
            mario.jumpAnim=mario.jumpAnim-1 
        end
        despook=mario.jumpAnim
        if mario.starAnim~=false then
            local animOption=(math.ceil((playStage.framesPassedBlock/mario.starAnim)))%4
            if animOption~=3 then
                if mario.powerAnim>0 then
                    mario.powerAnim=2
                else
                    mario.powerAnim=0
                end
                star="star"..animOption
            end
        end
        if mario.iFrames>playStage.framesPassed and not playStage.wait then --currently under influence of iframes
            local animOption=(math.ceil((playStage.framesPassed/flashingDelay)))%2
            if animOption==1 then mario.status="invisible" end
        end
        if mario.status~="invisible" then
            gc:drawImage(_G[mario.dir..mario.powerAnim..mario.status..star],mario.x-playStage.cameraOffset,mario.y-drawOffset+8) --draw..mario
        end
    end

    function mario:handleStomp()
        mario.hitCount=mario.hitCount+1
        if mario.hitCount~=0 then
            if mario.hitCount<#hitProgressionMario+1 then
                objAPI:addStats("points",hitProgressionMario[mario.hitCount],mario.x,mario.y-16)
            else --1up
                objAPI:addStats("1up",1,mario.x,mario.y-16)
            end
        end
    end


