objPlatform=class()

    function objPlatform:init(classID,posX,posY,TYPE,despawnable,arg1,arg2)  --platform_length~vel~MODE~distance eg, platform_3~2~lx~64
        
        self.oldVel={}
        local config=string.sub(TYPE,10,#TYPE)
        config=splitByChar(config,"~")
        self.length=config[1]
        self.speed=config[2]
        self.TYPE=config[3]
        if self.TYPE=="lx" or self.TYPE=="ly" then
            self.distance=tonumber(config[4])
            if self.distance<0 then self.distance=math.abs(self.distance) self.speed=-self.speed end
            self.distanceTracker=self.distance
        elseif self.TYPE=="ru" or self.TYPE=="rd" then
            self.distanceTracker=0
        end
        self.sort=string.sub(self.TYPE,#self.TYPE,#self.TYPE)
        self.mode=string.sub(self.TYPE,1,1)
        if self.sort=="l" or self.sort=="d" then self.speed=-math.abs(self.speed)
        elseif self.sort=="r" or self.sort=="u" then self.speed=math.abs(self.speed)
        end
        self.LEVEL="entityListOuter"
        self.classID=classID
        self.x=posX
        self.y=posY
        self.vx=0   self.vy=0
        self.oldVel.x=0     self.oldVel.y=0
        if self.mode=="a" or self.mode=="f" then
            self.active=false
        end
        self.despawnable=false
        self.GLOBAL=true --always drawn and logic applying, to prevent pop in
    end

    function objPlatform:logic() --handle both movement and animation
    --DESPAWN IF NEEDED
            if ((self.y<=-16) or( self.y>=204) or (self.x<=-(self.length*16)) or (self.x>=16*currentLevel.END)) and self.mode~="r" then
                objAPI:destroy(self.classID,self.LEVEL) return
            end
    --MOVE
            self.x=self.x+self.vx
            self.oldVel.x=self.vx
            self.y=self.y-self.vy
            self.oldVel.y=self.vy
    --CHECK IF MARIO COLLIDED
            if self.active==false then
                local marioSize=16
                if mario.power==0 or mario.crouch then marioSize=0 end
                local pX=self.x local pY=self.y+self.vy
                local pW=self.length*16 local mX=math.floor(mario.x+mario.px) local mY=math.floor(mario.y-mario.py)
                if ((mX+2>=pX and mX+2<=pX+pW) or (mX+13>=pX and mX+13<=pX+pW)) and mY+16==pY and mario.vy==0 then --mario is on
                    self.active=true
                end
            end
    --LOOPING PLATFORMS
            if self.mode=="l" then --LOOPING PLATFORMS
                if self.distanceTracker<0 then
                    self.distanceTracker=self.distance
                    self.speed=-self.speed
                    self["v"..self.sort]=0
                else
                    self.distanceTracker=self.distanceTracker-math.abs(self.speed)
                    self["v"..self.sort]=self.speed
                end
            else
                local dir="y"
                if self.sort=="l" or self.sort=="r" then
                    dir="x"
                end
                if self.mode=="a" or self.mode=="f" then --ONE DIRECTION PLATFORMS
                    if self.active==true then
                        self["v"..dir]=self.speed
                        if self.mode=="f" then --TODO
                            self.active=false
                        end
                    else
                        self["v"..dir]=0
                    end
                elseif self.mode=="r" then --REPEATING PLATFORMS
                    self["v"..dir]=self.speed
                    if self.y<=-18 and self.sort=="u" then
                        self.y=206
                    elseif self.y>=206 and self.sort=="d" then
                        self.y=-18
                    end
                end
            end
    --PLATFORM UPDATE
            objAPI:addPlatform(self.classID,self.x,self.y,self.length*16,self.vx,self.vy)
    end

    function objPlatform:draw(gc,EDITOR)
        if EDITOR==nil then
            for i=1, self.length do
                gc:drawImage(_G["texture_platform"],self.x-playStage.cameraOffset+(i-1)*16,self.y+8+self.oldVel.y)
            end
        else 
            local length=splitByChar(string.sub(EDITOR[3],10,#EDITOR[3]),"~")[1]
            local mode=splitByChar(string.sub(EDITOR[3],10,#EDITOR[3]),"~")[3]
            if EDITOR[4] then length=1 end
            for i=1, length do
                gc:drawImage(_G["texture_platform"],EDITOR[1]-editor.cameraOffset+(i-1)*16,EDITOR[2]+8)
            end
            gc:drawImage(_G["texture_icon_"..mode],EDITOR[1]-editor.cameraOffset,EDITOR[2]+8)--texture_icon_ru
            local plot=pixel2plot(EDITOR[1]+16,EDITOR[2],true,true)
            if editor.platformSelect and editor.platformSelect[3]==true and (plot[1]==editor.platformSelect[1] and plot[2]==editor.platformSelect[2]) then
                timer2rainbow(gc,framesPassed+200,10)
                gc:setPen("thin","dashed")
                local distance=tonumber(splitByChar(string.sub(EDITOR[3],10,#EDITOR[3]),"~")[4])
                if mode=="lx" then
                    gc:drawRect(EDITOR[1]-editor.cameraOffset+distance,EDITOR[2]+8,length*16,8)
                    gc:drawLine(EDITOR[1]-editor.cameraOffset,EDITOR[2]+12,EDITOR[1]-editor.cameraOffset+distance,EDITOR[2]+12)
                elseif mode=="ly" then
                    gc:drawRect(EDITOR[1]-editor.cameraOffset,EDITOR[2]+8-distance,length*16,8)
                    local offset={0,8}
                    if distance<0 then offset={8,0} end
                    gc:drawLine(EDITOR[1]-editor.cameraOffset+length*8,EDITOR[2]+8+offset[1],EDITOR[1]-editor.cameraOffset+length*8,EDITOR[2]+8-distance+offset[2])
                end
            end
        end
    end
