objGoomba=class()

    function objGoomba:init(classID,posX,posY,TYPE,despawnable,arg1,arg2) --eg ("goomba77215",64,64,"goomba")
        self.vy=0
        self.px=0 self.py=0
        self.LEVEL="entityListInner"
        self.TYPE=TYPE
        self.classID=classID
        self.x=posX
        self.y=posY
        self.status=1
        self.dead=false
        self.despawnable=false --for now, unless pipe spawning added
        self.hitBox={16,16,true,true}
        if mario.x>self.x then
            self.dir=1 -- -1 is left, 1 is right
        else self.dir=-1 end
    end

    function objGoomba:logic() --handle both movement and animation
        if not objAPI:aggregateCheckX(true,self.x,self.y,0,0,true) and not self.status==4 then --stuck in a block
            objAPI:destroy(self.classID,self.LEVEL) return end
        if not self.dead then
    --CHECK IF MARIO COLLIDED
            if not (mario.starTimer>playStage.framesPassed) then
                local marioSize=16
                if mario.power==0 or mario.crouch then marioSize=0 end
                if checkCollision(mario.x+1,mario.y-marioSize+1,14,14+marioSize, self.x+1,self.y,14,1) and mario.vy<=0 then --hit on head
                    self.dead=true
                    self.status=3
                    self.deathAnimTimer=playStage.framesPassed+10
                    mario.vtempY=15
                    mario:handleStomp()
                    objAPI:sendToFront(self.classID,self.LEVEL)
                elseif checkCollision(mario.x+1,mario.y-marioSize+1,14,14+marioSize, self.x+1,self.y+3,14,12) then --hit mario (side)
                    mario:powerDownMario()
                end
            end
    --X AXIS
            if objAPI:aggregateCheckX(false,self.x,self.y,(self.dir*2)+self.px,0,true) then
                self.x=self.x+(self.dir*2)+self.px
            else
                self.dir=-self.dir
                if objAPI:aggregateCheckX(false,self.x,self.y,(self.dir*2)+self.px,0,true) then
                    self.x=self.x+(self.dir*2)+self.px
                elseif not objAPI:aggregateCheckX(false,self.x,self.y,0,0,true) then
                    objAPI:destroy(self.classID,self.LEVEL) end
            end
    --Y AXIS
            if objAPI:aggregateCheckX(true,self.x,self.y-self.py,0,0) then
                self.y=self.y-self.py
            end
            if self.vy>0 then --ascending
                if self.vy>0.5 then --significant velocity
                    self.vy=self.vy*0.7
                else --small velocity
                    self.vy=-0.5
                end
            elseif self.vy<0 then --descending
                if self.vy>-6 then --significant velocity
                    self.vy=self.vy*1.5
                end
                if self.vy<-6 then
                    self.vy=-6
                    self.y=math.floor(self.y/6)*6
                end
            end
            if self.vy<=0 then --neutral/falling
                if objAPI:gravityCheck(false,self.x,self.y,0) then --not standing on floor
                    if self.vy==0 then
                        self.vy=-0.5
                    end
                    if objAPI:gravityCheck(false,self.x,self.y,-math.floor(self.vy)) then
                        self.y=self.y-math.floor(self.vy)
                    else
                        self.vy=math.floor(self.vy)
                        while not objAPI:gravityCheck(false,self.x,self.y,-self.vy) do
                            if self.vy<0 then
                                self.vy=self.vy+1
                            else break end
                        end
                        self.y=self.y-self.vy+1
                    end
                else --on floor
                    self.vy=0
                end
            else --ascending
                if objAPI:bumpCheck(false,self.x,self.y,self.vy) then
                    self.y=self.y-self.vy
                else
                    self.vy=-0.5 --fall down
                end
            end
    --PLATFORMS
            local platformVel=objAPI:platformCheck(self.x,self.y)
            self.px=platformVel[1]  self.py=platformVel[2]
    --ANIMATION
            if not self.dead then
                self.status=((math.ceil((framesPassed/4)))%2)+1
            end
        elseif self.status==4 then
            self.x=self.x+(2*self.dir)
            if self.vy<-0.5 then
                self.y=self.y+self.vy
                self.vy=(self.vy+0.2)*0.8
            elseif (self.vy<0 and self.vy>-0.5) or self.vy>0 then
                self.vy=(math.abs(self.vy)+0.3)*1.09
                self.y=self.y+self.vy
            end
            if self.y<0 then
                objAPI:destroy(self.classID,self.LEVEL)
            end
        elseif self.status==3 and (self.deathAnimTimer<playStage.framesPassed) then
            objAPI:destroy(self.classID,self.LEVEL)
        end
    end
    
    function objGoomba:hit(circumstance)
        if not self.dead then
            self.vy=-11
            self.dead=true
            self.status=4
            if circumstance=="fireball" or circumstance=="block" then
                objAPI:addStats("points","100",self.x,self.y)
            end
            if circumstance=="mario" or circumstance=="fireball" then
                if mario.x<self.x then
                    self.dir=1 -- -1 is left, 1 is right
                else self.dir=-1 end
            end
        end
    end

