objPiranhaPlant=class()

    function objPiranhaPlant:init(classID,posX,posY,TYPE,despawnable,arg1,arg2) --eg ("piranhaplant_1r2923",64,64,"piranhaplant_1",true)
        self.TYPE=TYPE -- 1=up  , 2=right  , 3=down  , 4=left 
        self.classID=classID
        self.move={}
        self.x=posX
        self.y=posY
        self.status=1
        self.dead=false
        self.despawnable=false
        self.LEVEL="entityListBackground"
        self.moveTimer=50 --how far into the rising thing it is
        self.riseTimer=5 --frames to wait before rising
        TYPE=string.sub(TYPE,14,14)
        if TYPE=="1" then --up
            self.move.x={"x",1}
            self.move.y={"y",-1}
        elseif TYPE=="2" then --right
            self.move.x={"y",1}
            self.move.y={"x",1}
            self:moveY(-16)
        elseif TYPE=="3" then --down
            self.move.x={"x",-1}
            self.move.y={"y",1}
            self:moveY(-16)
        elseif TYPE=="4" then --left
            self.move.x={"y",-1}
            self.move.y={"x",-1}
        end
        self:moveX(8)
        self:moveY(-10)
        self:determineHitbox()
        self.hitBox=self.hitBoxLocal
    end

    function objPiranhaPlant:moveX(amount) --moves on the x axis, relative to the direction the plant is facing
        self[self.move.x[1]]=self[self.move.x[1]]+self.move.x[2]*amount
    end

    function objPiranhaPlant:moveY(amount)
        self[self.move.y[1]]=self[self.move.y[1]]+self.move.y[2]*amount
    end

    function objPiranhaPlant:determineHitbox()
        local TYPE=string.sub(self.TYPE,14,14) --hardcoding is quicker, easier and more efficient than doing some cool code... i think
        if TYPE=="1" then --up
            self.hitBoxLocal={16,20,true,true,0,14} --V
        elseif TYPE=="2" then --right
            self.hitBoxLocal={20,16,true,true,-4,0}
        elseif TYPE=="3" then --down
            self.hitBoxLocal={16,20,true,true,0,-3} --
        elseif TYPE=="4" then --left
            self.hitBoxLocal={20,16,true,true,14,0}
        end
    end

    function objPiranhaPlant:hit(circumstance)
        if not self.dead then
            objAPI:transferLayer(self.classID,self.LEVEL,"entityListInner")
            self.vy=-11
            self.dead=true
            self.status=1
            if circumstance=="fireball" or circumstance=="block" then
                objAPI:addStats("points","100",self.x,self.y)
            end
            if mario.x<self.x then
                self.dir=1 -- -1 is left, 1 is right
            else self.dir=-1 end
            self.TYPE="piranhaplant_3" --essentially, flipping the image
        end
    end

    function objPiranhaPlant:logic() --handle both movement and animation
        if not self.dead then
    --CHECK IF MARIO COLLIDED
            if not (mario.starTimer>playStage.framesPassed) then
                local marioSize=16
                if mario.power==0 or mario.crouch then marioSize=0 end
                if checkCollision(mario.x+1,mario.y-marioSize+1,14,14+marioSize,self.x+2+self.hitBoxLocal[5],self.y+2+self.hitBoxLocal[6],self.hitBoxLocal[1]-4,self.hitBoxLocal[2]-4) then --hit mario (side)
                    mario:powerDownMario()
                end
            end
    --X/Y AXIS
            self.moveTimer=self.moveTimer+1
            if self.moveTimer<=12 then
                self:moveY(2)
            elseif self.moveTimer<=36 then
            elseif self.moveTimer<=48 then
                self:moveY(-2)
            else
                self.hitBox=false
                self.riseTimer=self.riseTimer-1
                local marioDistance=math.abs(mario.x-self.x)
                if marioDistance>=35 then
                    if self.riseTimer<=0 then
                        self.moveTimer=0
                        self.riseTimer=32
                        self.hitBox=self.hitBoxLocal
                    end
                end
            end
        end
    --ANIMATION
        if not self.dead then
            self.status=((math.ceil((framesPassed/4)))%2)+1
        else
            self.x=self.x+(2*self.dir)
            if self.vy<-0.5 then
                self.y=self.y+self.vy
                self.vy=(self.vy+0.2)*0.8
            elseif (self.vy<0 and self.vy>-0.5) or self.vy>0 then
                self.vy=(math.abs(self.vy)+0.3)*1.09
                self.y=self.y+self.vy
            end
        end
    end

    function objPiranhaPlant:draw(gc,EDITOR)
        if EDITOR==nil then
            gc:drawImage(_G["texture_"..self.TYPE.."_"..self.status],self.x-playStage.cameraOffset,self.y+8)
        else
            local status="_1"
            if EDITOR[3]=="piranhaplant_2" and not EDITOR[4] then EDITOR[1]=EDITOR[1]-16 end
            if EDITOR[3]=="piranhaplant_3" and not EDITOR[4] then EDITOR[2]=EDITOR[2]-16 end
            if EDITOR[4] then status="_icon" end
            gc:drawImage(_G["texture_"..EDITOR[3]..status],EDITOR[1]-editor.cameraOffset,EDITOR[2]+8)
        end
    end

