                gc:drawImage(_G["texture_"..EDITOR[3].."_icon"],EDITOR[1]-editor.cameraOffset,EDITOR[2]+8)
            else
                gc:drawImage(_G["texture_L_"..EDITOR[3].."_1"],EDITOR[1]-editor.cameraOffset,EDITOR[2]-8)
            end
        end
