objShell=class()

    function objShell:init(classID,posX,posY,TYPE,despawnable,velocity,framecount) --eg ("shell_g77215",64,64,"shell_g",-4)
        if velocity==nil or velocity==0 then 
            self.vx=0 
            if fromKoopa==true then self.koopaTimer=playStage.framesPassed+200 else self.koopaTimer=false end
        else self.vx=velocity self.koopaTimer=false end
        self.vy=0
        self.px=0 self.py=0
        self.LEVEL="entityListInner"
        self.TYPE=TYPE
        self.classID=classID
        self.fromKoopa=fromKoopa
        self.x=posX
        self.y=posY
