    function objShell:logic() --handle both movement and animation
        if not self.dead then
    --CHECK IF MARIO COLLIDED
            if not (mario.starTimer>playStage.framesPassed) then
                local marioSize=16
                if mario.power==0 or mario.crouch then marioSize=0 end            
                if checkCollision(mario.x+1,mario.y-marioSize+1,14,14+marioSize, self.x+1,self.y,14,1) and (mario.vy<0 or mario.py~=0) and (self.hitTimer<playStage.framesPassed) then --hit from above
                    self.hitTimer=playStage.framesPassed+6 --avoid instakill after kicking shell or double hits
                    mario:handleStomp()
                    self.hitCount=0 self.hitCountSTOR=0
                    local shakeCondition=true
                    if self.koopaTimer~=false then if (self.koopaTimer-45<playStage.framesPassed) then shakeCondition=false end end
                    if self.vx==0 and shakeCondition then
                        self.koopaTimer=false
                        mario.vtempY=8
                        if self.x>mario.x then
                            self.vx=4
                            self.x=math.ceil(self.x/4)*4 --align to grid
                        else
                            self.vx=-4
                            self.x=math.floor(self.x/4)*4 --align to grid
                        end
                    else self.vx=0 self.koopaTimer=playStage.framesPassed+200 mario.vtempY=15 end
                elseif checkCollision(mario.x+1,mario.y-marioSize+1,14,14+marioSize, self.x+1,self.y+3,14,12) and (self.hitTimer<playStage.framesPassed) then --hit mario from side
