            end
    --X AXIS
            if objAPI:aggregateCheckX(true,self.x,self.y,self.px,0) then
                self.x=self.x+self.px
            end
            if objAPI:aggregateCheckX(false,self.x,self.y,self.vx,0,true) then
                self.x=self.x+(self.vx)
            else
                local offset=0
                if self.vx>0 then offset=16 end
