            end
            if not objAPI:aggregateCheckX(false,self.x,self.y,0,0,true) then --stuck in a block
                objAPI:destroy(self.classID,self.LEVEL) end
            if self.vx~=0 then
                objAPI:coinCheck(self.x,self.y,0)
