            end
            if self.vy<=0 then --neutral/falling
                if objAPI:gravityCheck(false,self.x,self.y,0) then --not standing on floor
                    if self.vy==0 then
                        self.vy=-0.5
                    end
                    if objAPI:gravityCheck(false,self.x,self.y,-math.floor(self.vy)) then
                        self.y=self.y-math.floor(self.vy)
                    else
                        self.vy=math.floor(self.vy)
                        while not objAPI:gravityCheck(false,self.x,self.y,-self.vy) do
                            if self.vy<0 then
                                self.vy=self.vy+1
                            else break end
                        end
                        self.y=self.y-self.vy+1
                    end
                else --on floor
                    self.vy=0
                end
            else --ascending
                if objAPI:bumpCheck(false,self.x,self.y,self.vy) then
                    self.y=self.y-self.vy
                else
                    self.vy=-0.5 --fall down
                end
            end
    --PLATFORMS
            local platformVel=objAPI:platformCheck(self.x,self.y)
            self.px=platformVel[1]  self.py=platformVel[2]
    --ANIMATION
            if not self.dead then
                if self.koopaTimer==false then
                    self.status=1
                elseif self.fromKoopa then
                    if self.koopaTimer<playStage.framesPassed then
                        objAPI:createObj("koopa"..string.sub(self.TYPE,6,8),self.x,self.y)
                        objAPI:destroy(self.classID,self.LEVEL)
                        self.status=0
                    elseif (self.koopaTimer-7<playStage.framesPassed) then
                        self.status=2
                    elseif (self.koopaTimer-45<playStage.framesPassed) then
                        local animOption=((math.ceil((playStage.framesPassed)))%4)
                        if animOption==0 or animOption==2 then self.status=1
                        elseif animOption==1 then self.status=4
                        else self.status=5 end
                    else
                        self.status=1
                    end
                else
                    self.koopaTimer=false
                end
            end
        elseif self.dead then
            self.x=self.x+(2*self.dir)
            if self.vy<-0.5 then
                self.y=self.y+self.vy
                self.vy=(self.vy+0.2)*0.8
            elseif (self.vy<0 and self.vy>-0.5) or self.vy>0 then
                self.vy=(math.abs(self.vy)+0.3)*1.09
                self.y=self.y+self.vy
            end
        end
    end

    function objShell:handleShellPoints()
        self.hitCount=self.hitCount+1
        if self.hitCount~=0 then
            if self.hitCount<(#hitProgressionKoopa+1) then
                objAPI:addStats("points",hitProgressionKoopa[self.hitCount],self.x,self.y+16)
            else
                objAPI:addStats("1up",1,self.x,self.y+16)
            end
        end
    end

    function objShell:hit(circumstance)
        if not self.dead then
            self.vy=-11
            self.dead=true
            self.status=3
            self.koopaTimer=false
            if circumstance=="fireball" or circumstance=="block" then
                objAPI:addStats("points","100",self.x,self.y)
            end
            self.dir=1
            if circumstance=="mario" or circumstance=="fireball" or self.vx==0 then
                if mario.x<self.x then
                    self.dir=1 -- -1 is left, 1 is right
                else self.dir=-1 end
            else
                self.dir=self.vx/math.abs(self.vx)
            end
        end
    end

    function objShell:draw(gc,EDITOR)
        if EDITOR==nil then
        local offsetY=0
            if self.status~=0 then
                if not studentSoftware and (self.status==4 or self.status==5) then offsetY=-2 end
                gc:drawImage(_G["texture_"..self.TYPE.."_"..self.status],self.x-playStage.cameraOffset,self.y+8+offsetY)--"texture_shell_R_1"
            end 
        else
            gc:drawImage(_G["texture_"..EDITOR[3].."_1"],EDITOR[1]-editor.cameraOffset,EDITOR[2]+8)--"texture_shell_R_1"
        end
    end

