objPowerUp=class()

    function objPowerUp:init(classID,posX,posY,TYPE,fromBlock,arg1,arg2) --eg ("mushroom37253",64,16,"mushroom",true)
        self.px=0 self.py=0
        self.vy=0
        self.LEVEL="entityListInner"
        if string.sub(TYPE,1,1)=="P" then
            if mario.power==0 then
                self.TYPE="mushroom"
                self.status=""
            elseif mario.power>0 then
                self.TYPE="fireflower"
                self.status=1
            end
        else
            self.TYPE=TYPE
            if self.TYPE=="mushroom" or self.TYPE=="mushroom1up" then
                self.status=""
            else
                self.status=1
            end
        end
        self.classID=classID
        self.x=posX
        self.y=posY
        self.hitBox={16,16,false,false}
        self.despawnable=fromBlock
        if fromBlock==true then
            self.blockTimer=playStage.framesPassed+(4-1)
            self.y=self.y-4
        else self.blockTimer=playStage.framesPassed end
        if mario.x>self.x then
            self.dir=1 -- -1 is left, 1 is right
        else self.dir=-1 end
    end

    function objPowerUp:logic() --handle both movement and animation
    --CHECK IF MARIO COLLECTED
        local marioSize=16
        if mario.power==0 or mario.crouch then marioSize=0 end
        if self.blockTimer<playStage.framesPassed then
            if checkCollision(mario.x+1,mario.y-marioSize+1,14,14+marioSize, self.x+1,self.y+1,14,14) then
                objAPI:destroy(self.classID,self.LEVEL)
                if self.TYPE=="mushroom1up" then
                    objAPI:addStats("1up",1,self.x,self.y)
                else
                    if self.TYPE=="mushroom" then
                        mario:powerUpMario(1)
                    elseif self.TYPE=="fireflower" then
                        mario:powerUpMario(2)
                    elseif self.TYPE=="star" then
                        mario:powerStarMario()
                    end
                    objAPI:addStats("points",1000,self.x,self.y)
                end
                return
            end
    --X AXIS
            if objAPI:aggregateCheckX(true,self.x,self.y,self.px,0) then
                self.x=self.x+self.px
            end
            if self.TYPE~="fireflower" then
                if objAPI:aggregateCheckX(false,self.x,self.y,(self.dir*2),0,true) then
                    self.x=self.x+(self.dir*2)
                else
                    self.dir=-self.dir
                    if objAPI:aggregateCheckX(false,self.x,self.y,(self.dir*2),0,true,{8,8}) then
                        self.x=self.x+(self.dir*2)
                    elseif not objAPI:aggregateCheckX(false,self.x,self.y,0,0,true) then
                        objAPI:destroy(self.classID,self.LEVEL) return end
                end
            end
    --Y AXIS
            if objAPI:aggregateCheckX(true,self.x,self.y-self.py,0,0) then
                self.y=self.y-self.py
            end
            if self.vy>0 then --ascending
                if self.vy>0.5 then --significant velocity
                    self.vy=self.vy*0.7
                else --small velocity
                    self.vy=-0.5
                end
            elseif self.vy<0 then --descending
                if self.vy>-6 then --significant velocity
                    self.vy=self.vy*1.5
                end
                if self.vy<-6 then
                    self.vy=-6
                    self.y=math.floor(self.y/6)*6
                end
            end
            if self.vy<=0 then --neutral/falling
                if objAPI:gravityCheck(false,self.x,self.y,0) then --not standing on floor
                    if self.vy==0 then
                        self.vy=-0.5
                    end
                    if objAPI:gravityCheck(false,self.x,self.y,-math.floor(self.vy)) then
                        self.y=self.y-math.floor(self.vy)
                    else
                        self.vy=math.floor(self.vy)
                        while not objAPI:gravityCheck(false,self.x,self.y,-self.vy) do
                            if self.vy<0 then
                                self.vy=self.vy+1
                            else break end
                        end
                        self.y=self.y-self.vy+1
                    end
                elseif self.TYPE=="star" then --star
                    self.vy=10
                else --on floor
                    self.vy=0
                end
            else --ascending
                if objAPI:bumpCheck(false,self.x,self.y,self.vy) then
                    self.y=self.y-self.vy
                else
                    self.vy=-0.5 --fall down
                end
            end
    --PLATFORMS
            local platformVel=objAPI:platformCheck(self.x,self.y)
            self.px=platformVel[1]  self.py=platformVel[2]
    --ANIMATION
        else
            self.y=self.y-4 --rise from block
        end
        if self.TYPE=="fireflower" or self.TYPE=="star" then
            self.status=((math.ceil((framesPassed/flashingDelay)))%4)+1
        end
    end

    function objPowerUp:hit(circumstance)
        if self.vy<=0 and self.blockTimer<playStage.framesPassed then
            self.vy=10
        end
    end

    function objPowerUp:draw(gc,EDITOR)
        if EDITOR==nil then
            gc:drawImage(_G["texture_"..self.TYPE..self.status],self.x-playStage.cameraOffset,self.y+8)
        else
            local status=""
            if EDITOR[3]=="fireflower" or EDITOR[3]=="star" then status="1" end
            gc:drawImage(_G["texture_"..EDITOR[3]..status],EDITOR[1]-editor.cameraOffset,EDITOR[2]+8)
        end
    end

