    end

    function objFireball:gravityCheck(yVel) --returns true if nothing in way
        return (objAPI:moveCheckY(false,self.x,self.y+8,yVel,true) and objAPI:moveCheckY(false,self.x+7,self.y+8,yVel,true))
    end

    function objFireball:handleFireballHit()
        self.dead=true
        self.timer=1
        self.x=self.x-4
        self.y=self.y-4
        self.TYPE="fireball_A"
        self.status=1
    end

    function objFireball:logic()
        despook=("n")
        if not self.dead then
            objAPI:addHitBox(self.classID,self.x,self.y,8,8,"fireball")
    --X AXIS
            if objAPI:moveCheckX(true,self.x+4,self.y+2,self.dir*6,true) then
                self.x=self.x+(self.dir*6)
            else
                self:handleFireballHit() return
            end
    --Y AXIS
            if self.vy>0 then --ascending
                if self.vy>0.5 then --significant velocity
                    self.vy=self.vy*0.5
                else --small velocity
                    self.vy=-0.5
                end
            elseif self.vy<0 then --descending
                if self.vy>-4 then --significant velocity
                    self.vy=self.vy*1.7
                end
                if self.vy<-4 then
                    self.vy=-4
                    --self.y=math.floor(self.y/4)*4
                end
            end
            if self.vy<=0 then --neutral/falling
                if objAPI:gravityCheck(false,self.x,self.y-8,0,nil,nil,8) then --not standing on floor
                    if self.vy==0 then
                        self.vy=-0.5
                    end
                    if objAPI:gravityCheck(false,self.x,self.y-8,-math.floor(self.vy),nil,nil,8) then
                        self.y=self.y-math.floor(self.vy)
                    else
                        self.vy=math.floor(self.vy)
                        while not objAPI:gravityCheck(false,self.x,self.y-8,-self.vy,nil,nil,8) do
                            if self.vy<0 then
                                self.vy=self.vy+1
                            else break end
                        end
                        self.y=self.y-self.vy+1
                        self.vy=16
                    end
                else --on floor
                    self.vy=16
                end
            else --ascending
                if objAPI:bumpCheck(false,self.x,self.y,self.vy) then
                    self.y=self.y-self.vy
                else
                    self:handleFireballHit() return
                end
            end
    --DEAD
        else
            self.timer=self.timer+1
            if self.timer>=(flashingDelay*3)+1 then
                objAPI:destroy(self.classID,self.LEVEL) return
            end
        end
    --ANIMATION
        if not self.dead then
            self.status=((math.ceil((framesPassed/2)))%4)+1
        else
            self.status=math.ceil(self.timer/flashingDelay)
        end
    end

    function objFireball:draw(gc) --texture_fireball_R1
        gc:drawImage(_G["texture_"..self.TYPE..self.status],self.x-playStage.cameraOffset,self.y+8)
    end

